/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면저장
 * 주요변수 : 
 **********************************************************************/ 
function setOutSystem(){
	if(validationChk() == false) return false ;
	var sKind		= "setOutSystem";						
	var sMethodName = "service::user/outSystemSave.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_outSystem=ds_outSystem:U";     	//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    								// 조회 후 돌려 받을 DataSet 명칭
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, "", "callback");
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-02
 * 내용설명 : validation 체크
 * 주요변수 : 
 **********************************************************************/ 
function validationChk()
{

	for(j=0;j<ds_outSystem.rowcount;j++){

		if(length(trim(ds_outSystem.GetColumn(j, "COMPANY")))==0){
			alert(setPopMessage("2041"));	// 20110103l-koowgnojeel, 2041 == 사업부를 선택하세요.
			ds_outSystem.row = j;
			return false;
		}
		if(length(trim(ds_outSystem.GetColumn(j, "SYSTEM_SEQ")))==0){
			alert(setPopMessage("2042"));	// 20110103l-koowgnojeel, 2042 == 시스템 코드를 입력해 주세요.
			ds_outSystem.row = j;
			return false;
		}
		if(length(trim(ds_outSystem.GetColumn(j, "SYS_NAME")))==0){
			alert(setPopMessage("2038"));	// 20110103l-koowgnojeel, 2038 == 시스템명을 입력해 주세요.
			ds_outSystem.row = j;
			return false;
		}					
	}



	var chk = 0;
	for(i=0;i<ds_outSystem_tmp.rowcount;i++)
	{
		chk = 0;
		for(j=0;j<ds_outSystem.rowcount;j++)
		{
			if(ds_outSystem_tmp.GetColumn(i,"COMPANY") == ds_outSystem.GetColumn(j,"COMPANY")
				&& ds_outSystem_tmp.GetColumn(i,"SYSTEM_SEQ") == ds_outSystem.GetColumn(j,"SYSTEM_SEQ"))
			{
				chk++;
			}		
		}

		if(chk > 1)
		{
			alert(setPopMessage("2039"));	// 20110103l-koowgnojeel, 2039 == 중복된 데이터가 존재합니다.
			return false;
		}
	}
	return true;
}


function on_compChange(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_ComCode_PLNT.Filter("CODE1=='" + cb_company.Value + "'");
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getOutSystem"){
		// 조회건수가 없을경우
		if(ds_outSystem.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="setOutSystem"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}


function OnRowPosChange(obj,nOldRow,nRow)
{
	if(ds_outSystem.GetRowType(ds_outSystem.row) == "insert")
	{
		Edit1.Enable = true;
	} else {
		Edit1.Enable = false;
	}
}



