#include "script::lib_script_common.js";
var nowDate = today();


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getComCode();

	ed_syyyymmdd.Value = substr(nowDate,0,6) + "01";
	ed_eyyyymmdd.Value = nowDate;
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)					

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_COMP=ds_ComCode_COMP";
	var sArgument   = "";
		sArgument   += " CLASS='COMP'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	ds_VComCode_COMP.Copy("ds_ComCode_COMP");

	ds_ComCode_COMP.InsertRow(0);
	ds_ComCode_COMP.SetColumn(0,"CODE1","");
	ds_ComCode_COMP.SetColumn(0,"CODE_NAME","[전체]");
	cb_comp.index = 0;	




	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_PLNT.index = 0;	

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 조회
 * 주요변수 : 
 **********************************************************************/ 
function getMenuLog(){
	http.sync = true;
	var sKind		= "getMenuLog";						
	var sMethodName = "service::user/selectMenuLog.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             		// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_menuLog=ds_menuLog";    			// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID=" + quote(ed_searchUserId.Value);
	sArgument += " USER_NAME=" + quote(ed_searchUserName.Value);
	sArgument += " SDATE=" + quote(ed_syyyymmdd.Value);
	sArgument += " EDATE=" + quote(ed_eyyyymmdd.Value);
	sArgument += " COMPANY=" + quote(cb_comp.Value);
	sArgument += " PLANT_CD=" + quote(cb_PLNT.Value);		

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


function compChange(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(cb_comp.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_PLNT.index = 0;	

}





/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getMenuLog"){
		// 조회건수가 없을경우
		if(ds_menuLog.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}



