function setExcel()
{
	var fName   = "프로그램 사용 LOG";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|프로그램 사용 LOG";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


