SELECT A.CUSNO, C.CUSNM, A.HOUSE, SUM(A.MOHTQ) AS MOHTQ , SUM(A.MOHTQ * B.PRICE) AS SUM_PRICE FROM (SELECT D.CARCD , F.MSTSM , A.ITNBR , C.HOUSE , C.MOHTQ , COALESCE(G.CUSNO,0) CUSNO FROM $LIB1$.BM008PR A LEFT OUTER JOIN $LIB1$.HM000PR C ON A.ITNBR = C.ITNBR LEFT OUTER JOIN (select * from $LIB2$.MST100PF where COMPS = #COMPS#) D ON A.ITNBR = D.PRTNO LEFT OUTER JOIN (SELECT * FROM $LIB2$.MST050PF WHERE COMPS = #COMPS# AND GRPCD='CAR') F ON D.CARCD = F.MSTCD LEFT OUTER JOIN (SELECT Y.PRTNO,MAX(Y.CUSNO) CUSNO FROM $LIB1$.em000pr Y where Y.edatm = (select MAX(X.edatm) from $LIB1$.em000pr x where Y.prtno=x.prtno and x.EDATM <= $NDATE$) GROUP BY Y.PRTNO ) G ON A.ITNBR=G.PRTNO WHERE ( A.ITTYP = '1'
A.ITTYP = '9'
) AND C.MOHTQ != 0 AND SUBSTR(A.ITCLS,2)='1' ORDER BY D.CARCD ) A LEFT OUTER JOIN (SELECT A.CUSNO, A.PRTNO, A.EDATM, B.PRICE FROM (SELECT CUSNO, PRTNO, MAX(EDATM) AS EDATM FROM $LIB1$.EM001PR WHERE EDATM <= $NDATE$ GROUP BY CUSNO, PRTNO ORDER BY CUSNO, PRTNO ) A LEFT OUTER JOIN $LIB1$.EM001PR B ON A.CUSNO = B.CUSNO AND A.PRTNO = B.PRTNO AND A.EDATM = B.EDATM ) B ON A.ITNBR = B.PRTNO and a.cusno=b.cusno LEFT OUTER JOIN (SELECT A.CUSNO, A.CUSNM FROM $LIB1$.CUSMAS A ) C ON A.CUSNO = C.CUSNO WHERE A.HOUSE IN (select SUBSTR(MAJCD,6,1) from $LIB2$.mst040pf where MAJCD like 'HOUSE%' AND MJCHR=#COMPS#) GROUP BY A.CUSNO, C.CUSNM, A.HOUSE ORDER BY A.CUSNO,A.HOUSE

SELECT A.ITNBR AS ITNBR , A.ITDSC AS ITDSC , SUM(A.MOHTQ) AS MOHTQ , SUM(A.MOHTQ * B.PRICE) AS SUM_PRICE, COALESCE(C.ENDDT,0)ENDDT FROM (SELECT D.CARCD , F.MSTSM , A.ITNBR , A.ITDSC , C.HOUSE , C.MOHTQ, G.CUSNO FROM $LIB1$.BM008PR A LEFT OUTER JOIN $LIB1$.HM000PR C ON A.ITNBR = C.ITNBR LEFT OUTER JOIN (select * from $LIB2$.MST100PF where COMPS = #COMPS#) D ON A.ITNBR = D.PRTNO LEFT OUTER JOIN (SELECT * FROM $LIB2$.MST050PF WHERE COMPS = #COMPS# AND GRPCD='CAR') F ON D.CARCD = F.MSTCD LEFT OUTER JOIN (SELECT Y.PRTNO,MAX(Y.CUSNO) CUSNO FROM $LIB1$.em000pr Y where Y.edatm = (select MAX(X.edatm) from $LIB1$.em000pr x where Y.prtno=x.prtno and x.EDATM <= $NDATE$) GROUP BY Y.PRTNO ) G ON A.ITNBR=G.PRTNO WHERE ( A.ITTYP = '1'
A.ITTYP = '9'
) AND C.MOHTQ != 0 AND SUBSTR(A.ITCLS,2)='1'
C.HOUSE = #HOUSE#
COALESCE(G.CUSNO,0) = $CUSNO$
) A LEFT OUTER JOIN (SELECT A.CUSNO, A.PRTNO, A.EDATM, B.PRICE FROM (SELECT CUSNO, PRTNO, MAX(EDATM) AS EDATM FROM $LIB1$.EM001PR WHERE EDATM <= $NDATE$ GROUP BY CUSNO, PRTNO ORDER BY CUSNO, PRTNO ) A LEFT OUTER JOIN $LIB1$.EM001PR B ON A.CUSNO = B.CUSNO AND A.PRTNO = B.PRTNO AND A.EDATM = B.EDATM ) B ON A.ITNBR = B.PRTNO and a.cusno=b.cusno LEFT OUTER JOIN (SELECT A.PSPNO, MAX(A.ENDDT) AS ENDDT FROM $LIB2$.SMZ100PF A GROUP BY A.PSPNO ) C ON A.ITNBR = C.PSPNO GROUP BY A.ITNBR, A.ITDSC, C.ENDDT ORDER BY A.ITNBR

SELECT A.CUSNO, C.CUSNM , A.HOUSE , SUM(A.MOHTQ) AS MOHTQ , SUM(A.MOHTQ * B.PRICE) AS SUM_PRICE FROM (SELECT D.CARCD , F.MSTSM , A.ITNBR , C.HOUSE , C.MOHTQ , COALESCE(G.CUSNO,0) CUSNO FROM $LIB1$.BM008PR A LEFT OUTER JOIN $LIB2$.BDD100PF C ON A.ITNBR = C.ITNBR LEFT OUTER JOIN (select * from $LIB2$.MST100PF where COMPS = #COMPS#) D ON A.ITNBR = D.PRTNO LEFT OUTER JOIN (SELECT * FROM $LIB2$.MST050PF WHERE COMPS = #COMPS# AND GRPCD='CAR') F ON D.CARCD = F.MSTCD LEFT OUTER JOIN (SELECT Y.PRTNO,MAX(Y.CUSNO) CUSNO FROM $LIB1$.em000pr Y where Y.edatm = (select MAX(X.edatm) from $LIB1$.em000pr x where Y.prtno=x.prtno and x.EDATM <= $NDATE$) GROUP BY Y.PRTNO ) G ON A.ITNBR=G.PRTNO WHERE ( A.ITTYP = '1'
A.ITTYP = '9'
) AND C.MOHTQ != 0 AND SUBSTR(A.ITCLS,2)='1' ) A LEFT OUTER JOIN (SELECT A.CUSNO, A.PRTNO, A.EDATM, B.PRICE FROM (SELECT CUSNO, PRTNO, MAX(EDATM) AS EDATM FROM $LIB1$.EM001PR WHERE EDATM <= $NDATE$ GROUP BY CUSNO, PRTNO ORDER BY CUSNO, PRTNO ) A LEFT OUTER JOIN $LIB1$.EM001PR B ON A.CUSNO = B.CUSNO AND A.PRTNO = B.PRTNO AND A.EDATM = B.EDATM ) B ON A.ITNBR = B.PRTNO and a.cusno=b.cusno LEFT OUTER JOIN (SELECT A.CUSNO, A.CUSNM FROM $LIB1$.CUSMAS A ) C ON A.CUSNO = C.CUSNO WHERE A.HOUSE IN (select SUBSTR(MAJCD,6,1) from $LIB2$.mst040pf where MAJCD like 'HOUSE%' AND COMPS=#COMPANY#) GROUP BY A.CUSNO, C.CUSNM, A.HOUSE ORDER BY A.CUSNO,A.HOUSE

SELECT A.*, CASE WHEN A.SUPGU='01' THEN COALESCE(B.PRICE,0) WHEN A.SUPGU='02' OR A.SUPGU='03' THEN COALESCE(C.MNFCS,0) ELSE 0 END PRICE, CASE WHEN A.SUPGU='01' THEN COALESCE(B.PRICE,0)*BUJOK WHEN A.SUPGU='02' OR A.SUPGU='03' THEN COALESCE(C.MNFCS,0)*BUJOK ELSE 0 END BUJOKAMT FROM ( SELECT X.*, CASE WHEN Y.VCNTR != 'KR' AND SUBSTR(Y.VNDNR,1,1)='J' THEN '03' WHEN Y.VCNTR != 'KR' AND SUBSTR(Y.VNDNR,1,1)!='J' THEN '02' WHEN Y.VCNTR = 'KR' THEN '01' ELSE '' END SUPGU FROM (SELECT A.CONNO,trim(B.CUSNM) VNAME,A.SPRTN,trim(C.ITDSC) ITDSC,C.UNMSR,C.ITTYP,C.ITCLS, A.PHYOH,A.CTQTY,A.CTQTY-A.PHYOH AS BUJOK, CASE WHEN D.DPG1S1 IS NOT NULL THEN D.DPG1S1 WHEN E.PSPART IS NOT NULL THEN 'J01' ELSE '' END AS VNDNR FROM $LIB1$.HD150PR A LEFT OUTER JOIN (SELECT CUSNO,CUSNM FROM $LIB1$.CUSMAS) B ON A.CONNO=B.CUSNO LEFT OUTER JOIN (SELECT ITNBR,ITDSC,ITTYP,ITCLS,UNMSR FROM $LIB1$.BM008PR) C ON A.SPRTN=C.ITNBR LEFT OUTER JOIN (SELECT DPPRTN,DPG1S1 FROM $LIB1$.BM400PR) D ON A.SPRTN=D.DPPRTN LEFT OUTER JOIN (SELECT PSPART,'J01' FROM $LIB1$.BM012PR) E ON A.SPRTN=E.PSPART WHERE A.CONNO BETWEEN $CONNO1$ AND $CONNO2$ AND (A.PHYOH!= 0 OR A.CTQTY != 0)
A.PHYOH > A.CTQTY
) X LEFT OUTER JOIN (SELECT VNDNR,VCNTR FROM $LIB1$.VENNAM) Y ON X.VNDNR=Y.VNDNR ) A LEFT OUTER JOIN (SELECT PRTNO,SPCOD, CASE WHEN EDAT3 != 99999999 AND EDAT3 >= $YYMMDD$ THEN PRCE3 WHEN EDAT2 != 99999999 AND EDAT3 >= $YYMMDD$ THEN PRCE2 WHEN EDAT1 != 99999999 AND EDAT3 >= $YYMMDD$ THEN PRCE1 END PRICE FROM $LIB1$.CM001PR WHERE PRCLS='1' ) B ON A.SPRTN=B.PRTNO AND A.VNDNR=B.SPCOD LEFT OUTER JOIN (SELECT PRTNO,MNFCS FROM $LIB1$.$D30PR$) C ON A.SPRTN=C.PRTNO ORDER BY A.CONNO,A.SPRTN

SELECT A.CRYGU,A.CLSGU,A.PSPNO,B.ITDSC,B.UNMSR,B.ITTYP,B.ITCLS,A.HOUSE,D.WHSLC,A.PRICE, $IMQMM_B$ AS BEFQY,$IMIMM$ AS INPQY,$IMOMM$ AS OUTQY,$IMQMM$ JEGQY, DECIMAL($IMQMM_B$*A.PRICE,15,0) AS BEFAM,DECIMAL($IMIMM$*A.PRICE,15,0) AS INPAM,DECIMAL($IMOMM$*A.PRICE,15,0) AS OUTAM,DECIMAL($IMQMM$*A.PRICE,15,0) AS JEGAM,
CASE WHEN $IMQMM_B$ = 0 AND $IMQMM$ = 0 THEN 0 ELSE CASE WHEN $IMQMM_B$ = 0 OR DECIMAL($IMQMM$/$IMQMM_B$,15,4)*100 > 999.99 THEN 9999 ELSE DECIMAL($IMQMM$/$IMQMM_B$,15,4)*100 END END AS RATE FROM (SELECT COALESCE(C.MNFCS,0) MNFCS, COALESCE(E.CMPRICE,0) CMPRICE, CASE WHEN '$PRCGUB$' = '1' THEN COALESCE(C.MNFCS,0) ELSE COALESCE(E.CMPRICE,0) END PRICE, A.* FROM $LIB2$.BHZ500PF A LEFT OUTER JOIN (SELECT PRTNO,MNFCS FROM $LIB1$.$D30PR$) C ON A.PSPNO=C.PRTNO LEFT OUTER JOIN (SELECT V.PRTNO,V.SPCOD,V.EDATE,V.PRICE CMPRICE,V.CURCY CMCURCY,ROWNUMBER() OVER (PARTITION BY V.PRTNO) AS ROWNUM FROM (SELECT PRTNO,SPCOD, CASE WHEN EDAT3 != 99999999 AND EDAT3 <= $YMD$ THEN EDAT3 WHEN EDAT2 != 99999999 AND EDAT2 <= $YMD$ THEN EDAT2 WHEN EDAT1 != 99999999 AND EDAT1 <= $YMD$ THEN EDAT1 ELSE 0 END EDATE, CASE WHEN EDAT3 != 99999999 AND EDAT3 <= $YMD$ THEN PRCE3 WHEN EDAT2 != 99999999 AND EDAT2 <= $YMD$ THEN PRCE2 WHEN EDAT1 != 99999999 AND EDAT1 <= $YMD$ THEN PRCE1 ELSE 0 END PRICE, CASE WHEN EDAT3 != 99999999 AND EDAT3 <= $YMD$ THEN CRCY3 WHEN EDAT2 != 99999999 AND EDAT2 <= $YMD$ THEN CRCY2 WHEN EDAT1 != 99999999 AND EDAT1 <= $YMD$ THEN CRCY1 ELSE '' END CURCY FROM (SELECT PSPNO FROM $LIB2$.BHZ500PF WHERE IMHYY=$IMHYY$ COMPS=#COMPS#
CLSGU=#CLSGU#
)I , (SELECT * FROM $LIB1$.CM001PR WHERE PRCLS='1') J WHERE I.PSPNO=J.PRTNO)V, (SELECT PRTNO, MAX(CASE WHEN EDAT3 != 99999999 AND EDAT3 <= $YMD$ THEN EDAT3 WHEN EDAT2 != 99999999 AND EDAT2 <= $YMD$ THEN EDAT2 WHEN EDAT1 != 99999999 AND EDAT1 <= $YMD$ THEN EDAT1 ELSE 0 END) MAXEDATE FROM $LIB1$.CM001PR WHERE PRCLS='1' GROUP BY PRTNO)W WHERE V.PRTNO=W.PRTNO AND V.EDATE=W.MAXEDATE )E ON A.PSPNO=E.PRTNO AND E.ROWNUM=1 WHERE A.COMPS=#COMPS# AND A.IMHYY=$IMHYY$
A.CLSGU=#CLSGU#
) A LEFT OUTER JOIN (SELECT ITNBR,ITDSC,ITTYP,ITCLS,UNMSR FROM $LIB1$.BM008PR) B ON A.PSPNO=B.ITNBR LEFT OUTER JOIN (SELECT ITNBR,HOUSE,WHSLC FROM $LIB1$.HM000PR) D ON A.PSPNO=D.ITNBR AND A.HOUSE=D.HOUSE WHERE $IMQMM_B$!=0 OR $IMIMM$!=0 OR $IMOMM$!=0 OR $IMQMM$!=0 ORDER BY A.CRYGU,A.PSPNO

