		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}


function fn_print()
{
	if(confirm("확실히 "+st_RDOGUBNM.Text +" 제품 간판입니까? ") == false) return;

	if(ds_FPQC100QD.rowcount <= 0 || ds_FPQC100QD.GetRowType(ds_FPQC100QD.row) != 'normal') return ;

	ds_FPQC100Q_5D.ClearData();
	for (var row = 0 ; row < ds_FPQC100QD.RowCount ; row++ ){
		if(ds_FPQC100QD.GetSelect(row) == true){
			ds_FPQC100Q_5D.addRow();
			ds_FPQC100Q_5D.copyrow(ds_FPQC100Q_5D.row,"ds_FPQC100QD",row);
		}
	}
	//alert(ds_FPQC100Q_5D.SaveXML());
	if(ds_FPQC100Q_5D.rowcount <= 0) return ;
	alert(ds_FPQC100Q_5D.rowcount);
	var str = dialog("BARCODE::FPQE110QR.xml",
					" TABLE=" + quote(tableSelect) +
					" RDOGUB="+ quote(RDOGUB_D) +
					" COMPS=" + quote(ComboBoxPlant.Value) +
					" PNYMD=" + quote(cal_YMD.Value) +
					" CHKCHU="+ quote(CHKCHU_D) +
					" PRGUB=" + quote("PRINT_D") +
					" ds_SEL="+ quote(ds_FPQC100Q_5D.SaveXML()),1100,600);	
}
