function ds_MIZZ229_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "CATNO"){
		//중복Check
		if(ds_MIZZ229.FindRow("CATNO", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");	
			return false;
		}

		var sKind		= "getMIZZ220";						
		var sMethodName = "service::budget/getMIZZ220List";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ220=ds_MIZZ220";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " CATNO=" + quote(varNewValue);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ220.rowcount < 1){
			alert("경비예산 항목Master에 존재하지 않습니다.");
			return false;
		}
		catnm1_m = ds_MIZZ220.GetColumn(0,"CATNM1");
		catnm2_m = ds_MIZZ220.GetColumn(0,"CATNM2");
		catnm3_m = ds_MIZZ220.GetColumn(0,"CATNM3");
	}
	if(strColumnID == "VNDNR"){
		if(varNewValue == ""){
			alert("필수입력항목입니다.");
			return false;
		}
		var sKind		= "getMIZZ221";						
		var sMethodName = "service::budget/getMIZZ221List";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ221=ds_MIZZ221";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " VNDNR=" + quote(varNewValue);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ221.rowcount < 1){
			alert("경비예산 업체Master에 존재하지 않습니다.");
			return false;
		}
		vndnm_m = ds_MIZZ221.GetColumn(0,"VNDNM");
	}

	if(strColumnID == "NEWIT"){
		if(!(varNewValue == "Y" || varNewValue == "N")){
			alert(" Y,N 입력바랍니다.");
			return false;
		}
	}


}

function ds_MIZZ229_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "CATNO"){
		ds_MIZZ229.SetColumn(nRow,"CATNM1",catnm1_m);
		ds_MIZZ229.SetColumn(nRow,"CATNM2",catnm2_m);
		ds_MIZZ229.SetColumn(nRow,"CATNM3",catnm3_m);
	}

	if(strColumnID == "VNDNR"){
		ds_MIZZ229.SetColumn(nRow,"VNDNM",vndnm_m);
	}

	if(strColumnID == "COMPE"){
		if(varNewValue==""){
			ds_MIZZ229.SetColumn(nRow,strColumnID,"0");
		}
	}

	if(strColumnID == "CATQY"){
		if(varNewValue==""){
			ds_MIZZ229.SetColumn(nRow,strColumnID,"1");
		}
	}

	if(substr(strColumnID,0,3) == "AMT"){
		if(varNewValue==""){
			ds_MIZZ229.SetColumn(nRow,strColumnID,"0");
		}
		var amt04_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT04"));
		var amt05_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT05"));
		var amt06_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT06"));
		var amt07_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT07"));
		var amt08_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT08"));
		var amt09_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT09"));
		var amt10_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT10"));
		var amt11_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT11"));
		var amt12_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT12"));
		var amt01_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT01"));
		var amt02_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT02"));
		var amt03_t = toNumber(ds_MIZZ229.GetColumn(nRow,"AMT03"));
		var totAmt=0;
		switch(substr(strColumnID,3,2))
		{
			case "04" : 
			  totAmt = toNumber(varNewValue)+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "05" : 
			  totAmt = amt04_t+toNumber(varNewValue)+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "06" : 
			  totAmt = amt04_t+amt05_t+toNumber(varNewValue)+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "07" : 
			  totAmt = amt04_t+amt05_t+amt06_t+toNumber(varNewValue)+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "08" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+toNumber(varNewValue)+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "09" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+toNumber(varNewValue)+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "10" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+toNumber(varNewValue)+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "11" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+toNumber(varNewValue)+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "12" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+toNumber(varNewValue)+amt01_t+amt02_t+amt03_t;
			  break; 
			case "01" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+toNumber(varNewValue)+amt02_t+amt03_t;
			  break;
			case "02" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+toNumber(varNewValue)+amt03_t;
			  break;
			case "03" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+toNumber(varNewValue);
			  break;
			default:
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
		 }
		ds_MIZZ229.SetColumn(nRow,"CATAM",totAmt);
	}
}

