function MainGrid_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 1 && ds_MIZZ229.GetRowType(ds_MIZZ229.currow)=="insert") {
		var sTemp="zzzzzzzzzz";
		var selectnumber = "B4";
		var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
		if (length(rtn) != 0){
			ds_MIZZ229.SetColumn(nRow,"CATNO",rtn[0]);
		}
	}else if(nCell == 6) {
		var sTemp="zzzzzzzzzz";
		var selectnumber = "B3";
		var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
		if (length(rtn) != 0){
			ds_MIZZ229.SetColumn(nRow,"VNDNR",rtn[0]);
		}
	}else if(nCell == 13) {
		MainGrid.Enable = false;
		Div0.Visible = true;
		Div0.rdoSelect.Index = 0;
		Div0.me_CATAM.SetFocus();
	}else{
		if(ds_MIZZ229.rowcount <= 0) return ;
		var str = dialog("BUDGET::MIZZ229_0D.xml",
							" pCOMPS=" + quote(ComboBoxPlant.Value) +
							" pCATNO=" + quote(ds_MIZZ229.GetColumn(ds_MIZZ229.row,"CATNO")),1100,600);
		}
}

function imgSelect_OnClick(obj,nX,nY)
{
	Div0.Visible = false;
	MainGrid.Enable = true;
	var r = ds_MIZZ229.row;
	var tamt = toNumber(ds_MIZZ229.GetColumn(r,"CATAM"));
	if (Div0.rdoSelect.Value = "M12"){
		ds_MIZZ229.SetColumn(r,"AMT04",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT05",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT06",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT07",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT08",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT09",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT10",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT11",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT12",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT01",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT02",floor(Truncate(tamt/12)/10)*10);
		ds_MIZZ229.SetColumn(r,"AMT03",floor(Truncate(tamt/12)/10)*10);
	}
	if (Div0.rdoSelect.Value = "M4"){
		ds_MIZZ229.SetColumn(r,"AMT04",Truncate(tamt/4));
		ds_MIZZ229.SetColumn(r,"AMT05",0);
		ds_MIZZ229.SetColumn(r,"AMT06",0);
		ds_MIZZ229.SetColumn(r,"AMT07",tamt/4);
		ds_MIZZ229.SetColumn(r,"AMT08",0);
		ds_MIZZ229.SetColumn(r,"AMT09",0);
		ds_MIZZ229.SetColumn(r,"AMT10",tamt/4);
		ds_MIZZ229.SetColumn(r,"AMT11",0);
		ds_MIZZ229.SetColumn(r,"AMT12",0);
		ds_MIZZ229.SetColumn(r,"AMT01",tamt/4);
		ds_MIZZ229.SetColumn(r,"AMT02",0);
		ds_MIZZ229.SetColumn(r,"AMT03",0);
	}
	if (Div0.rdoSelect.Value = "M2"){
		ds_MIZZ229.SetColumn(r,"AMT04",tamt/2);
		ds_MIZZ229.SetColumn(r,"AMT05",0);
		ds_MIZZ229.SetColumn(r,"AMT06",0);
		ds_MIZZ229.SetColumn(r,"AMT07",0);
		ds_MIZZ229.SetColumn(r,"AMT08",0);
		ds_MIZZ229.SetColumn(r,"AMT09",0);
		ds_MIZZ229.SetColumn(r,"AMT10",tamt/2);
		ds_MIZZ229.SetColumn(r,"AMT11",0);
		ds_MIZZ229.SetColumn(r,"AMT12",0);
		ds_MIZZ229.SetColumn(r,"AMT01",0);
		ds_MIZZ229.SetColumn(r,"AMT02",0);
		ds_MIZZ229.SetColumn(r,"AMT03",0);
	}
	if (Div0.rdoSelect.Value = "M1"){
		Div1.Visible = false;
		var chkcnt = 0;
		var chkamt = 0;
		if (Div1.chk04P.Value = true) chkcnt++;
		if (Div1.chk05P.Value = true) chkcnt++;
		if (Div1.chk06P.Value = true) chkcnt++;
		if (Div1.chk07P.Value = true) chkcnt++;
		if (Div1.chk08P.Value = true) chkcnt++;
		if (Div1.chk09P.Value = true) chkcnt++;
		if (Div1.chk10P.Value = true) chkcnt++;
		if (Div1.chk11P.Value = true) chkcnt++;
		if (Div1.chk12P.Value = true) chkcnt++;
		if (Div1.chk01P.Value = true) chkcnt++;
		if (Div1.chk02P.Value = true) chkcnt++;
		if (Div1.chk03P.Value = true) chkcnt++;
		chkamt = tamt/chkcnt;
		ds_MIZZ229.SetColumn(r,"AMT04",iif(Div1.chk04P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT05",iif(Div1.chk05P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT06",iif(Div1.chk06P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT07",iif(Div1.chk07P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT08",iif(Div1.chk08P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT09",iif(Div1.chk09P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT10",iif(Div1.chk10P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT11",iif(Div1.chk11P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT12",iif(Div1.chk12P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT01",iif(Div1.chk01P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT02",iif(Div1.chk02P.Value = true,chkamt,0));
		ds_MIZZ229.SetColumn(r,"AMT03",iif(Div1.chk03P.Value = true,chkamt,0));
	}
}

function imgCancel_OnClick(obj,nX,nY)
{
	Div0.Visible = false;
	Div1.Visible = false;
	MainGrid.Enable = true;
	var r1 = ds_MIZZ229.row;
	var totAmt0 = 
		toNumber(ds_MIZZ229.GetColumn(r1,"AMT04"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT05"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT06"))+
		toNumber(ds_MIZZ229.GetColumn(r1,"AMT07"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT08"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT09"))+
		toNumber(ds_MIZZ229.GetColumn(r1,"AMT10"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT11"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT12"))+
		toNumber(ds_MIZZ229.GetColumn(r1,"AMT01"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT02"))+toNumber(ds_MIZZ229.GetColumn(r1,"AMT03"));
	ds_MIZZ229.SetColumn(r1,"CATAM",totAmt0);
}

