var nowDate = today();

function on_LoadCompleted(obj){
	getTitle();
	getDesign();
	getComCode();	
	SetYYYY13(substr(nowDate,0,4),ds_yyyy);
	cmb_yyyy.Value = substr(nowDate,0,4);
	//getCategory();
	//getMIZZ229Q6();
	gFcOnTopButtonColor("1"); 

	if(ComboBoxPlant.Value == "E1"){
		ed_DPTCD.Text = "1700";
	}else if(ComboBoxPlant.Value == "C1"){
		ed_DPTCD.Text = "3004";
	}else if(ComboBoxPlant.Value == "H1"){
		ed_DPTCD.Text = "3286";
	}else if(ComboBoxPlant.Value == "S1"){
		ed_DPTCD.Text = "2204";
	}else{
		ed_DPTCD.Text = "9999";
	}
	ed_DPTCD_OnChanged(ed_DPTCD,ed_DPTCD.Text);

	MainGrid.SetCellProp("Head", 7, "text", toNumber(cmb_yyyy.Value) - 1+"년 추정");
	MainGrid.SetCellProp("Head", 8, "text", toNumber(cmb_yyyy.Value) + "년 상반기 목표");
	MainGrid.SetCellProp("Head", 14, "text", toNumber(cmb_yyyy.Value) - 1+"年 實績");
	MainGrid.SetCellProp("Head", 15, "text", toNumber(cmb_yyyy.Value) - 1+"年 下修計");
	st_name.Text = toNumber(cmb_yyyy.Value) - 1+"년 추정";
	Static7.Text = "下修計";
}

function getComCode(){
	setCompsName(ds_ComCode_PLNT);
	//ds_ComCode_PLNT.InsertRow(0);
	//ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	//ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	ComboBoxPlant.Value=comps;
}

function getCategory(){

	var sKind		= "getCategory";						
	var sMethodName = "service::budget/getCategoryList"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_Category=ds_Category";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ds_Category.InsertRow(0);
	ds_Category.SetColumn(0,"CD","");
	ds_Category.SetColumn(0,"CDNM","[전체]");
	cmb_Category.index = 0;	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getMIZZ229Q6(){
	//cmb_Category.index = 0;

	var targetCATGB;
	var targetYYYY;
	if(cmb_CATGB.Value == "P0") {
		targetYYYY = toNumber(cmb_yyyy.Value) - 1;
		targetCATGB ="A0"; // 전년실적비교
	}else if(cmb_CATGB.Value == "P1") {
		targetYYYY = toNumber(cmb_yyyy.Value);
		targetCATGB ="P0"; // 상반기예산비교
	}else{
		alert("[경고] System오류. "); 
		return;
	}
	var groupcomp;
	var LIB99;
	if(ComboBoxPlant.Value == "E1"){
		groupcomp = "PE"; LIB99="CLTDTAE";
	}else if(ComboBoxPlant.Value == "C1"){
		groupcomp = "PS"; LIB99="CLTDTAS";
	}else if(ComboBoxPlant.Value == "H1"){
		groupcomp = "PS"; LIB99="CLTDTAS";
	}else if(ComboBoxPlant.Value == "S1"){
		groupcomp = "PS"; LIB99="CLTDTAS";
	}else if(ComboBoxPlant.Value == "R1"){
		groupcomp = "KR"; LIB99="CLTDTAE";
	}else{
		groupcomp = "";
	}
	http.Sync = true;
	var sKind		= "getMIZZ229Q6";						
	var sMethodName = "service::budget/getMIZZ229Q1List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZZ229Q1=ds_MIZZ229Q1";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);      //P0:상반기예산  P1:하반기예산 
		sArgument += " CATGB_YYYY=" + quote(targetYYYY);      // 전년실적비교 or 상반기예산비교
		sArgument += " CATGB_TARGET=" + quote(targetCATGB);   //A0:실적  P0:상반기예산
		sArgument += " DPTCD=" + quote(ed_DPTCD.Text);  // 부서코드
		sArgument += " GRCOM=" + quote(groupcomp);
		sArgument += " LIB99=" + quote(LIB99);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

	/*
	var str = dialog("Common::PGMLOAD_SHIP.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	*/

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		if (ds_MIZZ229Q1.rowcount > 0){
			calCHKFIELD();
		}
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
function calCHKFIELD(){
	for(var i=0;i<ds_MIZZ229Q1.rowcount;i++){
		var chked1=0;
		var chked2=0;
		if(cmb_CATGB.value = "P0"){
		/*	if(chk04P.Value==true) chked04=chked04+"AMT04B";
			if(chk05P.Value==true) chked05=chked05+"AMT05B";
			if(chk06P.Value==true) chked06=chked05+"AMT06B";
			if(chk07P.Value==true) chked07=chked04+"AMT07B";
			if(chk08P.Value==true) chked08=chked05+"AMT08B";
			if(chk09P.Value==true) chked09=chked05+"AMT09B";
			if(chk10P.Value==true) chked10=chked04+"AMT10B";
			if(chk11P.Value==true) chked11=chked05+"AMT11B";
			if(chk12P.Value==true) chked12=chked05+"AMT12B";
			if(chk01P.Value==true) chked01=chked04+"AMT01B";
			if(chk02P.Value==true) chked02=chked05+"AMT02B";
			if(chk03P.Value==true) chked03=chked05+"AMT03B";
		*/	
			if(chk04P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT04B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT04BB"));}
			if(chk05P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT05B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT05BB"));}
			if(chk06P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT06B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT06BB"));}
			if(chk07P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT07B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT07BB"));}
			if(chk08P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT08B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT08BB"));}
			if(chk09P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT09B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT09BB"));}
			if(chk10P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT10B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT10BB"));}
			if(chk11P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT11B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT11BB"));}
			if(chk12P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT12B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT12BB"));}
			if(chk01P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT01B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT01BB"));}
			if(chk02P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT02B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT02BB"));}
			if(chk03P.Value==true) {chked1+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT03B")); }else{chked2+=toNumber(ds_MIZZ229Q1.GetColumn(i,"AMT03BB"));}
			ds_MIZZ229Q1.SetColumn(i,"CATAMB",chked1+chked2);
		}
	}
}
/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName;
	if(cmb_CATGB.Value == "P0"){
		fName   = "상반기 경비예산목표 증감현황";
	}else{
		fName   = "하반기 경비예산목표 증감현황";
	}
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|경비예산목표증감현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

