function ed_DPTCD_OnChanged(obj,strText)
{
	if(length(trim(strText)) == 4){
		var Lib9="";
		if(ComboBoxPlant.Value == "E1"){
			Lib9="PSEDBLIB";
		}else if(ComboBoxPlant.Value == "R1"){
			Lib9="PSRDBLIB";
		}else{
			Lib9="PSCDBLIB";
		}
		var sKind		= "getMST650PFList";						
		var sMethodName = "service::budget/getMST650PFList"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST650PF=ds_MST650PF";
		var sArgument   = "";
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
			sArgument += " LIB9=" + quote(Lib9);
			sArgument += " DPTCD=" + quote(strText);
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		if(ds_MST650PF.rowcount > 0){
			st_DPTCD = ds_MST650PF.GetColumn(0,"DPDPNM");
			//ds_MIZZ229.ClearData();
			//ds_MIZZ220.ClearData();
			//ds_MIZZ221.ClearData();
			ds_MIZZ229Q1.ClearData();
		}else{
			st_DPTCD = "";
			alert("[경고] 부서코드를 확인바랍니다."); 
			return;
		}
	}else{
		st_DPTCD = "";
	}
}

function ComboBoxPlant_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_MST650PF.ClearData();
	if(ComboBoxPlant.Value == "E1"){
		ed_DPTCD.Text = "1700";
	}else if(ComboBoxPlant.Value == "C1"){
		ed_DPTCD.Text = "3004";
	}else if(ComboBoxPlant.Value == "H1"){
		ed_DPTCD.Text = "3286";
	}else if(ComboBoxPlant.Value == "S1"){
		ed_DPTCD.Text = "2204";
	}else{
		ed_DPTCD.Text = "9999";
	}
	ed_DPTCD_OnChanged(ed_DPTCD,ed_DPTCD.Text);
	ds_MIZZ229Q1.ClearData();
}
function cmb_CATGB_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(obj.value = "P0"){
		MainGrid.SetCellProp("Head", 7, "text", toNumber(cmb_yyyy.Value) - 1+"년 추정");
		MainGrid.SetCellProp("Head", 8, "text", toNumber(cmb_yyyy.Value) + "년 상반기 목표");
		MainGrid.SetCellProp("Head", 14, "text", toNumber(cmb_yyyy.Value) - 1+"年 實績");
		MainGrid.SetCellProp("Head", 15, "text", toNumber(cmb_yyyy.Value) - 1+"年 下修計");
		st_name.Text = toNumber(cmb_yyyy.Value) - 1+"년 추정";
		Static7.Text = "下修計";
		chk04P.Value = true; chk05P.Value = true; chk06P.Value = true; chk07P.Value = true; chk08P.Value = true; chk09P.Value = true;
		chk10P.Value = true; chk11P.Value = true; chk12P.Value = false; chk01P.Value = false; chk02P.Value = false; chk03P.Value = false;
		chk04C.Value = false; chk05C.Value = false; chk06C.Value = false; chk07C.Value = false; chk08C.Value = false; chk09C.Value = false;
		chk10C.Value = false; chk11C.Value = false; chk12C.Value = true; chk01C.Value = true; chk02C.Value = true; chk03C.Value = true;
	}else{
		MainGrid.SetCellProp("Head", 7, "text", toNumber(cmb_yyyy.Value) + "년 추정");
		MainGrid.SetCellProp("Head", 8, "text", toNumber(cmb_yyyy.Value) + "년 하반기 목표");
		MainGrid.SetCellProp("Head", 14, "text", toNumber(cmb_yyyy.Value) + "年 實績");
		MainGrid.SetCellProp("Head", 15, "text", toNumber(cmb_yyyy.Value) + "年 年計");
		st_name.Text = toNumber(cmb_yyyy.Value) + "년 추정";
		Static7.Text = "年計";
		chk04P.Value = true; chk05P.Value = true; chk06P.Value = true; chk07P.Value = false; chk08P.Value = false; chk09P.Value = false;
		chk10P.Value = false; chk11P.Value = false; chk12P.Value = false; chk01P.Value = false; chk02P.Value = false; chk03P.Value = false;
		chk04C.Value = false; chk05C.Value = false; chk06C.Value = false; chk07C.Value = true; chk08C.Value = true; chk09C.Value = true;
		chk10C.Value = true; chk11C.Value = true; chk12C.Value = true; chk01C.Value = true; chk02C.Value = true; chk03C.Value = true;
	}
	ds_MIZZ229Q1.ClearData();
	//MainGrid.tab2.Grid0.SetCellProp("Head", 26, "text", next4YY+"");
}

function cmb_yyyy_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cmb_CATGB.value = "P0"){
		MainGrid.SetCellProp("Head", 7, "text", toNumber(cmb_yyyy.Value) - 1+"년 추정");
		MainGrid.SetCellProp("Head", 8, "text", toNumber(cmb_yyyy.Value) + "년 상반기 목표");
		MainGrid.SetCellProp("Head", 14, "text", toNumber(cmb_yyyy.Value) - 1+"年 實績");
		MainGrid.SetCellProp("Head", 15, "text", toNumber(cmb_yyyy.Value) - 1+"年 下修計");
		st_name.Text = toNumber(cmb_yyyy.Value) - 1+"년 추정";
	}else{
		MainGrid.SetCellProp("Head", 7, "text", toNumber(cmb_yyyy.Value) + "년 추정");
		MainGrid.SetCellProp("Head", 8, "text", toNumber(cmb_yyyy.Value) + "년 하반기 목표");
		MainGrid.SetCellProp("Head", 14, "text", toNumber(cmb_yyyy.Value) + "年 實績");
		MainGrid.SetCellProp("Head", 15, "text", toNumber(cmb_yyyy.Value) + "年 年計");
		st_name.Text = toNumber(cmb_yyyy.Value) + "년 추정";
	}
	ds_MIZZ229Q1.ClearData();
}

function chkChange_OnClick(obj,strValue)
{
	if(obj.Value == false) object(left(obj.ID,5)+"C").Value = true;
	if(obj.Value == true) object(left(obj.ID,5)+"C").Value = false;
}
