/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getMIZZ229U(){
	var targetCATGB;
	if(cmb_CATGB.Value == "P0") {
		targetCATGB ="PA";
	}else if(cmb_CATGB.Value == "P1") {
		targetCATGB ="PB";
	}else{
		alert("[경고] System오류. "); 
		return;
	}
	http.Sync = true;
	var sKind		= "getMIZZ229U";						
	var sMethodName = "service::budget/getMIZZ229UList";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZZ229U=ds_MIZZ229U";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@CATYY=" + quote(cmb_yyyy.Value);
		sArgument += "@CATGB=" + quote(cmb_CATGB.Value);     //P0:상반기예산  P1:하반기예산 
		sArgument += "@CATGB_TARGET=" + quote(targetCATGB);   //PA: 상반기예산 목표  PB:하반기예산  목표
	var str = dialog("Common::PGMLOAD_SHIP.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}
/********************************************************************** 
 * 내용설명 : 화면저장
 **********************************************************************/ 
function setMIZZ229U(){
	if (ds_MIZZ229U.rowcount < 1){
		alert("[경고] 저장할 데이타가 존재하지 않습니다. "); 
		return;
	}

	// 확정 유무 Check
	if (!checkMIZ230()){
		alert("[경고] 경비예산이 확정되어 진행할 수 없습니다. 확인바랍니다."); 
		return;
	}

	if (ComboBoxPlant.Value == ""){
		if(confirm("사업부별 목표설정 및 DATA가 삭제됩니다. 작업하시겠습니까? ") == false) return;
	}

	var targetCATGB;
	if(cmb_CATGB.Value == "P0") {
		targetCATGB ="PA";
	}else if(cmb_CATGB.Value == "P1") {
		targetCATGB ="PB";
	}else{
		alert("[경고] System오류. "); 
		return;
	}
	var sKind		= "setMIZZ229U";						
	var sMethodName = "service::budget/setMIZZ229UList";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MIZZ229U=ds_MIZZ229U";     //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "ds_MIZZ229U_CHECK=ds_MIZZ229U_CHECK";     //자료 저장 시 보낼 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB_SOURCE=" + quote(cmb_CATGB.Value);     //P0:상반기예산  P1:하반기예산 
		sArgument += " CATGB_TARGET=" + quote(targetCATGB);   //PA: 상반기예산 목표  PB:하반기예산  목표
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MIZZ229U_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MIZZ229U_CHECK.GetRowCount();i++){
			alert("항목코드 : " +ds_MIZZ229U_CHECK.GetColumn(i,"CATNO") + " = " + ds_MIZZ229U_CHECK.GetColumn(i,"RTMSG"));
		}
		getMIZZ229U();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : 확정Check
 **********************************************************************/ 
 function checkMIZ230(){
	http.Sync = true;
	var sKind		= "getMIZZ230";						
	var sMethodName = "service::budget/getMIZZ230List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZ230=ds_MIZ230";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);   //P0:상반기예산  P1:하반기예산 
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if(ds_MIZ230.rowcount > 0){
		return false;
	}else{
		return true;
	}
 }

