function ds_MIZZ229U_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "TARPE"){
		if(varNewValue==""){
			ds_MIZZ229U.SetColumn(nRow,strColumnID,"0");
		}
	}
}

function fn_total1(){
	var total,beforeAM;
	for(var i=0;i<ds_MIZZ229U.GetRowCount();i++){
		var column1 = toNumber(ds_MIZZ229U.GetColumn(i,"CATAM_Y"));
		var column2 = toNumber(ds_MIZZ229U.GetColumn(i,"TARPE"));
		total += (column1*(100-column2)/100);
		beforeAM += column1;
	}
	st_s1.Text = ParseInt(100-(total/beforeAM*100)) + "% ↓";
	return total;
} 

function fn_total2(){
	var total,beforeAM;
	for(var i=0;i<ds_MIZZ229U.GetRowCount();i++){
		var column1 = toNumber(ds_MIZZ229U.GetColumn(i,"CATAM_Y"));
		var column2 = toNumber(ds_MIZZ229U.GetColumn(i,"TARPE"));
		var column3 = toNumber(ds_MIZZ229U.GetColumn(i,"CATAM"));
		total += column3-column1+(column1*(100-column2)/100);
		beforeAM += column3;
	}
	st_s2.Text = ParseInt(100-(total/beforeAM*100)) + "% ↓";
	return total;
} 

