var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();

function on_LoadCompleted(obj){
	getTitle();
	//getDesign();
	getComCode();	
	SetYYYY13(substr(nowDate,0,4),ds_yyyy);
	cmb_yyyy.Value = substr(nowDate,0,4);
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부 (조회,저장,excel)	
	if(ComboBoxPlant.Value == "E1"){
		ed_DPTCD.Text = "1700";
	}else if(ComboBoxPlant.Value == "C1"){
		ed_DPTCD.Text = "3004";
	}else if(ComboBoxPlant.Value == "H1"){
		ed_DPTCD.Text = "3286";
	}else if(ComboBoxPlant.Value == "S1"){
		ed_DPTCD.Text = "2204";
	}else{
		ed_DPTCD.Text = "9999";
	}
	ed_DPTCD_OnChanged(ed_DPTCD,ed_DPTCD.Text);
}

function getComCode(){
	setCompsName(ds_ComCode_PLNT);
	//ds_ComCode_PLNT.InsertRow(0);
	//ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	//ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	ComboBoxPlant.Value=comps;
}

function getCategory(){

	var sKind		= "getCategory";						
	var sMethodName = "service::budget/getCategoryList"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_Category=ds_Category";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ds_Category.InsertRow(0);
	ds_Category.SetColumn(0,"CD","");
	ds_Category.SetColumn(0,"CDNM","[전체]");
	cmb_Category.index = 0;	
}

function getDesign(){
	//MainGrid_229UP.ToolTipText = setPopMessage("2053");
	MainGrid_229UP.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid_229UP.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid_229UP.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid_229UP.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getMIZZ229UP(){
	if(length(trim(ComboBoxPlant.Value)) < 1){
		alert("[경고] 사업부를 확인바랍니다."); 
		return;
	}
	if(length(ed_DPTCD.Text) != 4 || length(trim(st_DPTCD.Text)) < 1){
		alert("[경고] 부서코드를 확인바랍니다."); 
		return;
	}
	// 확정 유무 Check
	if (!checkMIZ230()){
		alert("[경고] 경비예산이 확정되어 진행할 수 없습니다. 확인바랍니다."); 
		return;
	}

	ds_SEND.ClearData();
	ds_ERCHK.ClearData();
	FileDialog0.Filter = "ALL File(.xls)|*.xls";	
	FileDialog0.Type = "OPEN";
	if (!FileDialog0.Open()) {
		alert(setPopMessage("1009"));	//  파일 선택 창을 여는데 실패하였습니다.
		return;
	}
	var file_name = FileDialog0.FileName;
	var file_path = FileDialog0.FilePath;
	var path = file_path + "\\" + file_name;
	setMessageDirect(path);	
	ed_path.Text = path;
	ext_ExcelImportByIndex(path,0,"ds_SEND",1,1,0,2);  

	if (ds_SEND.GetRowCount() < 1){
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}
	ds_SEND.Sort("CATNO");

	chgExcelData();  //excel data  소수점 절삭

	chkExcelData();  //excel data 유효성 검사 
}
/********************************************************************** 
 * 내용설명 : 확정Check
 **********************************************************************/ 
 function checkMIZ230(){
	http.Sync = true;
	var sKind		= "getMIZZ230";						
	var sMethodName = "service::budget/getMIZZ230List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZ230=ds_MIZ230";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if(ds_MIZ230.rowcount > 0){
		return false;
	}else{
		return true;
	}
 }

/********************************************************************** 
 * 내용설명 : Excel data 10단위 절삭.
 **********************************************************************/  
function chgExcelData(){
	for(var i=0;i<ds_SEND.GetRowCount();i++){

		var iCATQY = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"CATQY"))));
		var iAMT04 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT04")))/10)*10;
		var iAMT05 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT05")))/10)*10;
		var iAMT06 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT06")))/10)*10;
		var iAMT07 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT07")))/10)*10;
		var iAMT08 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT08")))/10)*10;
		var iAMT09 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT09")))/10)*10;
		var iAMT10 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT10")))/10)*10;
		var iAMT11 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT11")))/10)*10;
		var iAMT12 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT12")))/10)*10;
		var iAMT01 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT01")))/10)*10;
		var iAMT02 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT02")))/10)*10;
		var iAMT03 = floor(Truncate(toNumber(ds_SEND.GetColumn(i,"AMT03")))/10)*10;
		ds_SEND.SetColumn(i,"CATQY",iCATQY);
		ds_SEND.SetColumn(i,"AMT04",iAMT04);
		ds_SEND.SetColumn(i,"AMT05",iAMT05);
		ds_SEND.SetColumn(i,"AMT06",iAMT06);
		ds_SEND.SetColumn(i,"AMT07",iAMT07);
		ds_SEND.SetColumn(i,"AMT08",iAMT08);
		ds_SEND.SetColumn(i,"AMT09",iAMT09);
		ds_SEND.SetColumn(i,"AMT10",iAMT10);
		ds_SEND.SetColumn(i,"AMT11",iAMT11);
		ds_SEND.SetColumn(i,"AMT12",iAMT12);
		ds_SEND.SetColumn(i,"AMT01",iAMT01);
		ds_SEND.SetColumn(i,"AMT02",iAMT02);
		ds_SEND.SetColumn(i,"AMT03",iAMT03);
		ds_SEND.SetColumn(i,"CATAM",iAMT04+iAMT05+iAMT06+iAMT07+iAMT08+iAMT09+iAMT10+iAMT11+iAMT12+iAMT01+iAMT02+iAMT03);
	}
}
/********************************************************************** 
 * 내용설명 : Excel data Check
 **********************************************************************/  
function chkExcelData(){
	var rRow; 
	var rCATNO;
	for(var z=0;z<ds_SEND.GetRowCount();z++){
		//중복Check
		if(trim(ds_SEND.GetColumn(z,"CATNO")) == trim(rCATNO)){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",1);
			ds_ERCHK.SetColumn(rRow,"ERRNM","중복 Error");
		}
		rCATNO = (ds_SEND.GetColumn(z,"CATNO"));
		var rVNDNR = (ds_SEND.GetColumn(z,"VNDNR"));
		var rCOMPE = (ds_SEND.GetColumn(z,"COMPE"));
		var rNEWIT = (ds_SEND.GetColumn(z,"NEWIT"));
		var rCATQY = (ds_SEND.GetColumn(z,"CATQY"));
		var rCATAM = (ds_SEND.GetColumn(z,"CATAM"));
		var rAMT04 = rtrim(ds_SEND.GetColumn(z,"AMT04"));
		var rAMT05 = rtrim(ds_SEND.GetColumn(z,"AMT05"));
		var rAMT06 = rtrim(ds_SEND.GetColumn(z,"AMT06"));
		var rAMT07 = rtrim(ds_SEND.GetColumn(z,"AMT07"));
		var rAMT08 = rtrim(ds_SEND.GetColumn(z,"AMT08"));
		var rAMT09 = rtrim(ds_SEND.GetColumn(z,"AMT09"));
		var rAMT10 = rtrim(ds_SEND.GetColumn(z,"AMT10"));
		var rAMT11 = rtrim(ds_SEND.GetColumn(z,"AMT11"));
		var rAMT12 = rtrim(ds_SEND.GetColumn(z,"AMT12"));
		var rAMT01 = rtrim(ds_SEND.GetColumn(z,"AMT01"));
		var rAMT02 = rtrim(ds_SEND.GetColumn(z,"AMT02"));
		var rAMT03 = rtrim(ds_SEND.GetColumn(z,"AMT03"));
		var rR2294 = rtrim(ds_SEND.GetColumn(z,"R2294"));

		// 항목Master존재 유무Check
		var sKind		= "getMIZZ220";						
		var sMethodName = "service::budget/getMIZZ220List";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ220=ds_MIZZ220";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " CATNO=" + quote(rCATNO);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ220.rowcount < 1){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",1);
			ds_ERCHK.SetColumn(rRow,"ERRNM","항목Master에 존재하지 않습니다.r");
		}

		//length check  = 01
		if(length(rCATNO) < 1 || length(rCATNO) > 15){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",1);
			ds_ERCHK.SetColumn(rRow,"ERRNM","항목코드 Length Error");
		}
		/*
		if(length(rVNDNR) < 1 || length(rVNDNR) > 3){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",2);
			ds_ERCHK.SetColumn(rRow,"ERRNM","업체코드 Length Error");
		}
		*/
		if(!numberChk(rCOMPE) || toNumber(rCOMPE) > 999){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",3);
			ds_ERCHK.SetColumn(rRow,"ERRNM","배분율 Error");
		}
		if(!(rNEWIT == "Y" || rNEWIT == "N")) {
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",4);
			ds_ERCHK.SetColumn(rRow,"ERRNM","신규구분 Error Y/N 선택");
		}
		//숫자check
		if (!numberChk(rCATQY) || !numberChk(rCATAM) || 
		    !numberChk(rAMT04) || !numberChk(rAMT05) || !numberChk(rAMT06) || !numberChk(rAMT07) || !numberChk(rAMT08) || !numberChk(rAMT09) ||
		    !numberChk(rAMT10) || !numberChk(rAMT11) || !numberChk(rAMT12) || !numberChk(rAMT01) || !numberChk(rAMT02) || !numberChk(rAMT03)){ 
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",0);
			ds_ERCHK.SetColumn(rRow,"ERRNM","숫자 Error");
		}
		if(toNumber(rCATQY) > 9999999999){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",5);
			ds_ERCHK.SetColumn(rRow,"ERRNM","수량 Over Error");
		}
		if(toNumber(rCATAM) != toNumber(rAMT04)+toNumber(rAMT05)+toNumber(rAMT06)+toNumber(rAMT07)+toNumber(rAMT08)+toNumber(rAMT09)+
		    toNumber(rAMT10)+toNumber(rAMT11)+toNumber(rAMT12)+toNumber(rAMT01)+toNumber(rAMT02)+toNumber(rAMT03)){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",6);
			ds_ERCHK.SetColumn(rRow,"ERRNM","총금액과 월금액합계 Error");
		}
		if(toNumber(rCATAM) > 999999999999999){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",6);
			ds_ERCHK.SetColumn(rRow,"ERRNM","총금액 Over Error");
		}
		if(toNumber(rAMT04) > 9999999999999 || toNumber(rAMT05) > 9999999999999 ||toNumber(rAMT06) > 9999999999999 ||
		   toNumber(rAMT07) > 9999999999999 || toNumber(rAMT08) > 9999999999999 ||toNumber(rAMT09) > 9999999999999 ||
		   toNumber(rAMT10) > 9999999999999 || toNumber(rAMT11) > 9999999999999 ||toNumber(rAMT12) > 9999999999999 ||
		   toNumber(rAMT01) > 9999999999999 || toNumber(rAMT02) > 9999999999999 ||toNumber(rAMT03) > 9999999999999){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",0);
			ds_ERCHK.SetColumn(rRow,"ERRNM","월금액 Over Error");
		}
		if(length(rR2294) > 10){
			rRow=ds_ERCHK.InsertRow();
			ds_ERCHK.SetColumn(rRow,"X",z+1);
			ds_ERCHK.SetColumn(rRow,"Y",18);
			ds_ERCHK.SetColumn(rRow,"ERRNM","비고 5문자만");
		}
	}
	ds_ERCHK.Sort("X");

}

 /********************************************************************** 
 * 내용설명 : Excel data Host UP
 **********************************************************************/  
function setMIZZ229UP(){
	if (ds_ERCHK.rowcount > 0){
		alert("오류항목을 수정후 다시 Host UP하세요... Host UP 불가능");
		return;
	}
	if (ds_SEND.rowcount < 1){
		alert("DATA가 없습니다. Host UP하세요... ");
		return;
	}
	if(confirm("기존data는 삭제합니다. 생성하시겠습니까? ") == false) return;

	for(var i=0;i<ds_SEND.GetRowCount();i++){
		if(trim(ds_SEND.GetColumn(i,"VNDNR")) == "" || ds_SEND.GetColumn(i,"VNDNR") == null){
			ds_SEND.SetColumn(i,"VNDNR","V99");
		}
		if(trim(ds_SEND.GetColumn(i,"COMPE")) == "" || ds_SEND.GetColumn(i,"COMPE") == null){
			ds_SEND.SetColumn(i,"COMPE","0");
		}
		if(trim(ds_SEND.GetColumn(i,"CATQY")) == "" || ds_SEND.GetColumn(i,"CATQY") == null){
			ds_SEND.SetColumn(i,"CATQY","0");
		}
		if(trim(ds_SEND.GetColumn(i,"CATAM")) == "" || ds_SEND.GetColumn(i,"CATAM") == null){
			ds_SEND.SetColumn(i,"CATAM","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT04")) == "" || ds_SEND.GetColumn(i,"AMT04") == null){
			ds_SEND.SetColumn(i,"AMT04","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT05")) == "" || ds_SEND.GetColumn(i,"AMT05") == null){
			ds_SEND.SetColumn(i,"AMT05","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT06")) == "" || ds_SEND.GetColumn(i,"AMT06") == null){
			ds_SEND.SetColumn(i,"AMT06","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT07")) == "" || ds_SEND.GetColumn(i,"AMT07") == null){
			ds_SEND.SetColumn(i,"AMT07","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT08")) == "" || ds_SEND.GetColumn(i,"AMT08") == null){
			ds_SEND.SetColumn(i,"AMT08","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT09")) == "" || ds_SEND.GetColumn(i,"AMT09") == null){
			ds_SEND.SetColumn(i,"AMT09","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT10")) == "" || ds_SEND.GetColumn(i,"AMT10") == null){
			ds_SEND.SetColumn(i,"AMT10","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT11")) == "" || ds_SEND.GetColumn(i,"AMT11") == null){
			ds_SEND.SetColumn(i,"AMT11","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT12")) == "" || ds_SEND.GetColumn(i,"AMT12") == null){
			ds_SEND.SetColumn(i,"AMT12","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT01")) == "" || ds_SEND.GetColumn(i,"AMT01") == null){
			ds_SEND.SetColumn(i,"AMT01","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT02")) == "" || ds_SEND.GetColumn(i,"AMT02") == null){
			ds_SEND.SetColumn(i,"AMT02","0");
		}
		if(trim(ds_SEND.GetColumn(i,"AMT03")) == "" || ds_SEND.GetColumn(i,"AMT03") == null){
			ds_SEND.SetColumn(i,"AMT03","0");
		}
	}
	var sKind		= "setMIZZ229UP";						
	var sMethodName = "service::budget/setMIZZ229UPList";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SEND=ds_SEND";     //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "ds_MIZZ229UP_CHECK=ds_MIZZ229UP_CHECK";     //자료 저장 시 보낼 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);
		sArgument += " DPTCD=" + quote(ed_DPTCD.Text);
		sArgument += " USERID=" + quote(trim(G_ds_user.GetColumn(0,"USER_ID")));
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MIZZ229UP_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MIZZ229UP_CHECK.GetRowCount();i++){
			alert("항목코드 : " +ds_MIZZ229UP_CHECK.GetColumn(i,"CATNO") + " = " + ds_MIZZ229UP_CHECK.GetColumn(i,"RTMSG"));
		}
		getMIZZ229UP();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

