/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getMIZZ239(){
	//cmb_Category.index = 0;	
	if(length(ed_DPTCD.Text) != 4 || length(trim(st_DPTCD.Text)) < 1){
		alert("[경고] 부서코드를 확인바랍니다."); 
		return;
	}
	var targetCATGB;
	if(cmb_CATGB.Value == "A0") {
		targetCATGB ="P0"; // 상반기 확정예산 비교
	}else if(cmb_CATGB.Value == "A1") {
		targetCATGB ="P1"; // 하반기 확정예산 비교
	}else{
		alert("[경고] System오류. "); 
		return;
	}
	http.Sync = true;
	var sKind		= "getMIZZ239";						
	var sMethodName = "service::budget/getMIZZ239List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZZ239=ds_MIZZ239";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@CATYY=" + quote(cmb_yyyy.Value);
		sArgument += "@CATGB=" + quote(cmb_CATGB.Value);   //A0:상반기예산  A1:하반기예산 
		sArgument += "@T_CATGB=" + quote(targetCATGB);   //P0:상반기예산  P1:하반기예산 
		sArgument += "@DPTCD=" + quote(ed_DPTCD.Text);  // 부서코드
	var str = dialog("Common::PGMLOAD_SHIP.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}
/********************************************************************** 
 * 내용설명 : 화면저장
 **********************************************************************/ 
function setMIZZ239(){

	// 확정 유무 Check
	if (!checkMIZ230()){
		alert("[경고] 경비예산이 확정되어 진행할 수 없습니다. 확인바랍니다."); 
		return;
	}
	if(length(ed_DPTCD.Text) != 4 || length(trim(st_DPTCD.Text)) < 1){
		alert("[경고] 부서코드를 확인바랍니다."); 
		return;
	}
	var sKind		= "setMIZZ239";
	var sMethodName = "service::budget/setMIZZ239List";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MIZZ239=ds_MIZZ239:U";     //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "ds_MIZZ239_CHECK=ds_MIZZ239_CHECK";     //자료 저장 시 보낼 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);   //P0:상반기예산  P1:하반기예산 
		sArgument += " DPTCD=" + quote(ed_DPTCD.Text);  // 부서코드
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MIZZ239_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MIZZ239_CHECK.GetRowCount();i++){
			alert("항목코드 : " +ds_MIZZ239_CHECK.GetColumn(i,"CATNO") + " = " + ds_MIZZ239_CHECK.GetColumn(i,"RTMSG"));
		}
		getMIZZ239();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : 확정Check
 **********************************************************************/ 
 function checkMIZ230(){
	http.Sync = true;
	var sKind		= "getMIZZ230";						
	var sMethodName = "service::budget/getMIZZ230List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZ230=ds_MIZ230";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(cmb_yyyy.Value);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);   //P0:상반기예산  P1:하반기예산 
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if(ds_MIZ230.rowcount > 0){
		return false;
	}else{
		return true;
	}
 }

