function ds_MIZZ239_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "CATNO"){
		//중복Check
		if(ds_MIZZ239.FindRow("CATNO", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");	
			return false;
		}
		//예산항목명
		var sKind		= "getMIZZ220";						
		var sMethodName = "service::budget/getMIZZ220List";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ220=ds_MIZZ220";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " CATNO=" + quote(varNewValue);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ220.rowcount < 1){
			alert("경비예산 항목Master에 존재하지 않습니다.");
			return false;
		}
		catnm1_m = ds_MIZZ220.GetColumn(0,"CATNM1");
		catnm2_m = ds_MIZZ220.GetColumn(0,"CATNM2");
		catnm3_m = ds_MIZZ220.GetColumn(0,"CATNM3");

		//예산 확정계획
		var targetCATGB;
		if(cmb_CATGB.Value == "A0") {
			targetCATGB ="P0"; // 상반기 확정예산 비교
		}else if(cmb_CATGB.Value == "A1") {
			targetCATGB ="P1"; // 하반기 확정예산 비교
		}else{
			alert("[경고] System오류. "); 
			return;
		}
		var sKind		= "getMIZZ239_occ";						
		var sMethodName = "service::budget/getMIZZ239_occList";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ239_occ=ds_MIZZ239_occ";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
			sArgument += " CATYY=" + quote(cmb_yyyy.Value);
			sArgument += " CATGB=" + quote(targetCATGB);   //P0:상반기예산  P1:하반기예산 
			sArgument += " CATNO=" + quote(varNewValue); //항목코드
			sArgument += " DPTCD=" + quote(ed_DPTCD.Text);  // 부서코드
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ239_occ.rowcount > 0){
			vndnr_m = ds_MIZZ239_occ.GetColumn(0,"VNDNR");
			vndnm_m = ds_MIZZ239_occ.GetColumn(0,"VNDNM");
			COMPE_m = ds_MIZZ239_occ.GetColumn(0,"COMPE");
			NEWIT_m = ds_MIZZ239_occ.GetColumn(0,"NEWIT");
			CATQY_m = ds_MIZZ239_occ.GetColumn(0,"CATQY");
			CATAM_m = ds_MIZZ239_occ.GetColumn(0,"CATAM");
			AMT04_m = ds_MIZZ239_occ.GetColumn(0,"AMT04");
			AMT05_m = ds_MIZZ239_occ.GetColumn(0,"AMT05");
			AMT06_m = ds_MIZZ239_occ.GetColumn(0,"AMT06");
			AMT07_m = ds_MIZZ239_occ.GetColumn(0,"AMT07");
			AMT08_m = ds_MIZZ239_occ.GetColumn(0,"AMT08");
			AMT09_m = ds_MIZZ239_occ.GetColumn(0,"AMT09");
			AMT10_m = ds_MIZZ239_occ.GetColumn(0,"AMT10");
			AMT11_m = ds_MIZZ239_occ.GetColumn(0,"AMT11");
			AMT12_m = ds_MIZZ239_occ.GetColumn(0,"AMT12");
			AMT01_m = ds_MIZZ239_occ.GetColumn(0,"AMT01");
			AMT02_m = ds_MIZZ239_occ.GetColumn(0,"AMT02");
			AMT03_m = ds_MIZZ239_occ.GetColumn(0,"AMT03");
		}
	}
	if(strColumnID == "VNDNR"){
		if(varNewValue == ""){
			alert("필수입력항목입니다.");
			return false;
		}
		var sKind		= "getMIZZ221";						
		var sMethodName = "service::budget/getMIZZ221List";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ221=ds_MIZZ221";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " VNDNR=" + quote(varNewValue);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ221.rowcount < 1){
			alert("경비예산 업체Master에 존재하지 않습니다.");
			return false;
		}
		vndnm_m = ds_MIZZ221.GetColumn(0,"VNDNM");
	}

	if(strColumnID == "NEWIT"){
		if(!(varNewValue == "Y" || varNewValue == "N")){
			alert(" Y,N 입력바랍니다.");
			return false;
		}
	}


}

