		}
		var sKind		= "getMIZZ239_occ";						
		var sMethodName = "service::budget/getMIZZ239_occList";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_MIZZ239_occ=ds_MIZZ239_occ";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
			sArgument += " CATYY=" + quote(cmb_yyyy.Value);
			sArgument += " CATGB=" + quote(targetCATGB);   //P0:상반기예산  P1:하반기예산 
			sArgument += " CATNO=" + quote(varNewValue); //항목코드
			sArgument += " DPTCD=" + quote(ed_DPTCD.Text);  // 부서코드
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MIZZ239_occ.rowcount > 0){
			ds_MIZZ239.SetColumn(nRow,"P0_VNDNR",vndnm_m);
			ds_MIZZ239.SetColumn(nRow,"P0_COMPE",COMPE_m);
			ds_MIZZ239.SetColumn(nRow,"P0_NEWIT",NEWIT_m);
			ds_MIZZ239.SetColumn(nRow,"P0_CATQY",CATQY_m);
			ds_MIZZ239.SetColumn(nRow,"P0_CATAM",CATAM_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT04",AMT04_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT05",AMT05_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT06",AMT06_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT07",AMT07_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT08",AMT08_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT09",AMT09_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT10",AMT10_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT11",AMT11_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT12",AMT12_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT01",AMT01_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT02",AMT02_m);
			ds_MIZZ239.SetColumn(nRow,"P0_AMT03",AMT03_m);
			alert(ds_MIZZ239_occ.Contents);      
			alert(ds_MIZZ239.Contents);
		}
		*/
	}

	if(strColumnID == "VNDNR"){
		ds_MIZZ239.SetColumn(nRow,"VNDNM",vndnm_m);
	}

	if(strColumnID == "COMPE"){
		if(varNewValue==""){
			ds_MIZZ239.SetColumn(nRow,strColumnID,"0");
		}
	}

	if(strColumnID == "CATQY"){
		if(varNewValue==""){
			ds_MIZZ239.SetColumn(nRow,strColumnID,"1");
		}
	}

	if(substr(strColumnID,0,3) == "AMT"){
		if(varNewValue==""){
			ds_MIZZ239.SetColumn(nRow,strColumnID,"0");
		}
		var amt04_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT04"));
		var amt05_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT05"));
		var amt06_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT06"));
		var amt07_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT07"));
		var amt08_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT08"));
		var amt09_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT09"));
		var amt10_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT10"));
		var amt11_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT11"));
		var amt12_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT12"));
		var amt01_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT01"));
		var amt02_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT02"));
		var amt03_t = toNumber(ds_MIZZ239.GetColumn(nRow,"AMT03"));
		var totAmt=0;
		switch(substr(strColumnID,3,2))
		{
			case "04" : 
			  totAmt = toNumber(varNewValue)+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "05" : 
			  totAmt = amt04_t+toNumber(varNewValue)+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "06" : 
			  totAmt = amt04_t+amt05_t+toNumber(varNewValue)+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "07" : 
			  totAmt = amt04_t+amt05_t+amt06_t+toNumber(varNewValue)+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "08" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+toNumber(varNewValue)+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "09" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+toNumber(varNewValue)+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "10" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+toNumber(varNewValue)+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "11" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+toNumber(varNewValue)+amt12_t+amt01_t+amt02_t+amt03_t;
			  break;
			case "12" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+toNumber(varNewValue)+amt01_t+amt02_t+amt03_t;
			  break; 
			case "01" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+toNumber(varNewValue)+amt02_t+amt03_t;
			  break;
			case "02" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+toNumber(varNewValue)+amt03_t;
			  break;
			case "03" : 
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+toNumber(varNewValue);
			  break;
			default:
			  totAmt = amt04_t+amt05_t+amt06_t+amt07_t+amt08_t+amt09_t+amt10_t+amt11_t+amt12_t+amt01_t+amt02_t+amt03_t;
		 }
		ds_MIZZ239.SetColumn(nRow,"CATAM",totAmt);
	}
}

function ds_MIZZ239_CanRowPosChange(obj,nOldRow,nRow)
{
	if(ds_MIZZ239.GetRowType(ds_MIZZ239.currow)=="insert" ){
		var p_CATNO=ds_MIZZ239.GetColumn(ds_MIZZ239.currow,"CATNO");
		var p_VNDNR=ds_MIZZ239.GetColumn(ds_MIZZ239.currow,"VNDNR");
		if (p_CATNO==null || p_VNDNR==null){
			alert("항목코드,업체는 필수입력입니다.");
			return false;
		}
	}
}

function ed_DPTCD_OnChanged(obj,strText)
{
	if(length(trim(strText)) == 4){
		var Lib9="";
		if(ComboBoxPlant.Value == "E1"){
			Lib9="PSEDBLIB";
		}else if(ComboBoxPlant.Value == "R1"){
			Lib9="PSRDBLIB";
		}else{
			Lib9="PSCDBLIB";
		}
		var sKind		= "getMST650PFList";						
		var sMethodName = "service::budget/getMST650PFList"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST650PF=ds_MST650PF";
		var sArgument   = "";
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
			sArgument += " LIB9=" + quote(Lib9);
			sArgument += " DPTCD=" + quote(strText);
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		if(ds_MST650PF.rowcount > 0){
			st_DPTCD = ds_MST650PF.GetColumn(0,"DPDPNM");
			ds_MIZZ239.ClearData();
			ds_MIZZ220.ClearData();
			ds_MIZZ221.ClearData();
		}else{
			st_DPTCD = "";
			alert("[경고] 부서코드를 확인바랍니다."); 
			return;
		}
	}else{
		st_DPTCD = "";
	}
}

function ComboBoxPlant_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_SEND.ClearData();
	//ds_ERCHK.ClearData();
	ds_MST650PF.ClearData();
	if(ComboBoxPlant.Value == "E1"){
		ed_DPTCD.Text = "1700";
	}else if(ComboBoxPlant.Value == "C1"){
		ed_DPTCD.Text = "3004";
	}else if(ComboBoxPlant.Value == "H1"){
		ed_DPTCD.Text = "3286";
	}else if(ComboBoxPlant.Value == "S1"){
		ed_DPTCD.Text = "2204";
	}else{
		ed_DPTCD.Text = "9999";
	}
	ed_DPTCD_OnChanged(ed_DPTCD,ed_DPTCD.Text);
	ds_MIZZ239.ClearData();
	ds_MIZZ220.ClearData();
	ds_MIZZ221.ClearData();
}
