function on_LoadCompleted(obj){
	getTitle();
	getDesign();
	getComCode();	

	//cal_YYMM.Value = substr(nowDate,0,6);

	gFcOnTopButtonColor("1"); 

	if(ComboBoxPlant.Value == "E1"){
		ed_DPTCD.Text = "1700";
	}else if(ComboBoxPlant.Value == "C1"){
		ed_DPTCD.Text = "3004";
	}else if(ComboBoxPlant.Value == "H1"){
		ed_DPTCD.Text = "3286";
	}else if(ComboBoxPlant.Value == "S1"){
		ed_DPTCD.Text = "2204";
	}else{
		ed_DPTCD.Text = "9999";
	}
	ed_DPTCD_OnChanged(ed_DPTCD,ed_DPTCD.Text);
}

function getComCode(){
	setCompsName(ds_ComCode_PLNT);
	//ds_ComCode_PLNT.InsertRow(0);
	//ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	//ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	ComboBoxPlant.Value=comps;
}

function getCategory(){

	var sKind		= "getCategory";						
	var sMethodName = "service::budget/getCategoryList"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_Category=ds_Category";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ds_Category.InsertRow(0);
	ds_Category.SetColumn(0,"CD","");
	ds_Category.SetColumn(0,"CDNM","[전체]");
	cmb_Category.index = 0;	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64
	//MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getMIZZ239Q1(){

	MainGrid.SetCellProp("Head", 7, "text", "~ " + substr(cal_YYMM.Value,4,2)+"월 누계");
	var tyyyy = toNumber(substr(cal_YYMM.Value,0,4));
	var mm = toNumber(substr(cal_YYMM.Value,4,2));
	var yyyy;
	if (mm<04){
		yyyy = tyyyy-1;
	}else{
		yyyy = tyyyy;
	}
	var groupcomp;
	var LIB99;
	if(ComboBoxPlant.Value == "E1"){
		groupcomp = "PE"; LIB99="CLTDTAE";
	}else if(ComboBoxPlant.Value == "C1"){
		groupcomp = "PS"; LIB99="CLTDTAS";
	}else if(ComboBoxPlant.Value == "H1"){
		groupcomp = "PS"; LIB99="CLTDTAS";
	}else if(ComboBoxPlant.Value == "S1"){
		groupcomp = "PS"; LIB99="CLTDTAS";
	}else if(ComboBoxPlant.Value == "R1"){
		groupcomp = "KR"; LIB99="CLTDTAE";
	}else{
		groupcomp = "";
	}

	http.Sync = true;
	var sKind		= "getMIZZ239Q1";						
	var sMethodName = "service::budget/getMIZZ239Q1List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MIZZ239Q1=ds_MIZZ239Q1";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " CATYY=" + quote(yyyy);
		sArgument += " CATGB=" + quote(cmb_CATGB.Value);      //P0:상반기예산  P1:하반기예산 
		sArgument += " DPTCD=" + quote(ed_DPTCD.Text);  // 부서코드
		sArgument += " GRCOM=" + quote(groupcomp);
		sArgument += " LIB99=" + quote(LIB99);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

	/*
	var str = dialog("Common::PGMLOAD_SHIP.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	*/

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName;
	if(cmb_CATGB.Value == "P0"){
		fName   = "년계 계획대실적_"+substr(cal_YYMM.Value,0,6);
	}else{
		fName   = "수계 계획대실적_"+substr(cal_YYMM.Value,0,6);
	}
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|계획대실적";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

