function ed_DPTCD_OnChanged(obj,strText)
{
	if(length(trim(strText)) == 4){
		var Lib9="";
		if(ComboBoxPlant.Value == "E1"){
			Lib9="PSEDBLIB";
		}else if(ComboBoxPlant.Value == "R1"){
			Lib9="PSRDBLIB";
		}else{
			Lib9="PSCDBLIB";
		}
		var sKind		= "getMST650PFList";						
		var sMethodName = "service::budget/getMST650PFList"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST650PF=ds_MST650PF";
		var sArgument   = "";
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
			sArgument += " LIB9=" + quote(Lib9);
			sArgument += " DPTCD=" + quote(strText);
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		if(ds_MST650PF.rowcount > 0){
			st_DPTCD = ds_MST650PF.GetColumn(0,"DPDPNM");
			ds_MIZZ239Q1.ClearData();
		}else{
			st_DPTCD = "";
			alert("[경고] 부서코드를 확인바랍니다."); 
			return;
		}
	}else{
		st_DPTCD = "";
	}
}

function ComboBoxPlant_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_MST650PF.ClearData();
	if(ComboBoxPlant.Value == "E1"){
		ed_DPTCD.Text = "1700";
	}else if(ComboBoxPlant.Value == "C1"){
		ed_DPTCD.Text = "3004";
	}else if(ComboBoxPlant.Value == "H1"){
		ed_DPTCD.Text = "3286";
	}else if(ComboBoxPlant.Value == "S1"){
		ed_DPTCD.Text = "2204";
	}else{
		ed_DPTCD.Text = "9999";
	}
	ed_DPTCD_OnChanged(ed_DPTCD,ed_DPTCD.Text);
	ds_MIZZ239Q1.ClearData();
}
function cmb_CATGB_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_MIZZ239Q1.ClearData();
}

function cmb_yyyy_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_MIZZ239Q1.ClearData();
}

function subTotal(gub,row){
	var retv = 0;
	//var row = ds_MIZZ239Q1.currow;
	var t04 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT04")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT04")));
	var t05 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT05")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT05")));
	var t06 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT06")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT06")));
	var t07 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT07")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT07")));
	var t08 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT08")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT08")));
	var t09 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT09")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT09")));
	var t10 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT10")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT10")));
	var t11 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT11")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT11")));
	var t12 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT12")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT12")));
	var t01 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT01")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT01")));
	var t02 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT02")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT02")));
	var t03 = iif(gub="P0",toNumber(ds_MIZZ239Q1.GetColumn(row,"P0_AMT03")),toNumber(ds_MIZZ239Q1.GetColumn(row,"AMT03")));
	switch (substr(cal_YYMM.Value,4,2)){
	case "04":
		retv=t04;break;
	case "05":
		retv=t04+t05;break;
	case "06":
		retv=t04+t05+t06;break;
	case "07":
		retv=t04+t05+t06+t07;break;
	case "08":
		retv=t04+t05+t06+t07+t08;break;
	case "09":
		retv=t04+t05+t06+t07+t08+t09;break;
	case "10":
		retv=t04+t05+t06+t07+t08+t09+t10;break;
	case "11":
		retv=t04+t05+t06+t07+t08+t09+t10+t11;break;
	case "12":
		retv=t04+t05+t06+t07+t08+t09+t10+t11+t12;break;
	case "01":
		retv=t04+t05+t06+t07+t08+t09+t10+t11+t12+t01;break;
	case "02":
		retv=t04+t05+t06+t07+t08+t09+t10+t11+t12+t01+t02;break;
	case "03":
		retv=t04+t05+t06+t07+t08+t09+t10+t11+t12+t01+t02+t03;break;
	}
	return retv;
} 
