function subTotalCHA(){
	var retv = 0;
	//var row = ds_MIZZ239Q1.currow;
	var t04 = toNumber(ds_MIZZ239Q1.sum("P0_AMT04")) - toNumber(ds_MIZZ239Q1.sum("AMT04"));
	var t05 = toNumber(ds_MIZZ239Q1.sum("P0_AMT05")) - toNumber(ds_MIZZ239Q1.sum("AMT05"));
	var t06 = toNumber(ds_MIZZ239Q1.sum("P0_AMT06")) - toNumber(ds_MIZZ239Q1.sum("AMT06"));
	var t07 = toNumber(ds_MIZZ239Q1.sum("P0_AMT07")) - toNumber(ds_MIZZ239Q1.sum("AMT07"));
	var t08 = toNumber(ds_MIZZ239Q1.sum("P0_AMT08")) - toNumber(ds_MIZZ239Q1.sum("AMT08"));
	var t09 = toNumber(ds_MIZZ239Q1.sum("P0_AMT09")) - toNumber(ds_MIZZ239Q1.sum("AMT09"));
	var t10 = toNumber(ds_MIZZ239Q1.sum("P0_AMT10")) - toNumber(ds_MIZZ239Q1.sum("AMT10"));
	var t11 = toNumber(ds_MIZZ239Q1.sum("P0_AMT11")) - toNumber(ds_MIZZ239Q1.sum("AMT11"));
	var t12 = toNumber(ds_MIZZ239Q1.sum("P0_AMT12")) - toNumber(ds_MIZZ239Q1.sum("AMT12"));
	var t01 = toNumber(ds_MIZZ239Q1.sum("P0_AMT01")) - toNumber(ds_MIZZ239Q1.sum("AMT01"));
	var t02 = toNumber(ds_MIZZ239Q1.sum("P0_AMT02")) - toNumber(ds_MIZZ239Q1.sum("AMT02"));
	var t03 = toNumber(ds_MIZZ239Q1.sum("P0_AMT03")) - toNumber(ds_MIZZ239Q1.sum("AMT03"));
	switch (substr(cal_YYMM.Value,4,2)){
	case "04":
		retv=t04;break;
	case "05":
		retv=t04+t05;break;
	case "06":
		retv=t04+t05+t06;break;
	case "07":
		retv=t04+t05+t06+t07;break;
	case "08":
		retv=t04+t05+t06+t07+t08;break;
	case "09":
		retv=t04+t05+t06+t07+t08+t09;break;
	case "10":
		retv=t04+t05+t06+t07+t08+t09+t10;break;
	case "11":
		retv=t04+t05+t06+t07+t08+t09+t10+t11;break;
	case "12":
