function IE0_NewWindow2(obj,ppDisp,Cancel)
{
	var cnt = Global.Windows.Count;
	var formname = "nf" + cnt++;
	//alert(formname);
	var xx  = '<?xml version="1.0" encoding="utf-8"?>\n';
	xx += '<Window>\n';
	xx += '<Form BKColor="" Height="768" Id="';
	xx += formname;
	xx += '" PidAttrib="7" OnInit="form_OnInit" Title="New&#32;Form" Ver="1.0" Width="1024" WorkArea="true">\n';
	xx += '<MSIE Bottom="768" Height="767" Id="IE0" Left="1" RegisterAsBrowser="true" Right="800" Top="1" Width="1023">\n</MSIE>\n';
	xx += '</Form><Script><![CDA';
	xx += 'TA[\n';
// script code
	xx += 'function form_OnInit(obj,nInitWidth,nInitHeight)\n';
	xx += '{\n';		
	xx += '}\n';	
	xx += ']]';
	xx += '></Script></Window>';

	Open( xx, "", -1,-1, "CloseFlag=true Resize=true",-1, -1);    

	ppDisp = Global.Windows(cnt-1).IE0.Application;
}
