#include "script::lib_script_common.js";


/********************************************************************** 
 * 개 발 자 : 이상건
 * 내용설명 : 화면 로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj)
{
	ed_select.Text = CODE;
	if (CODE == "zzzzzzzzzz") ed_select.Text="";
	switch (substr(SEL,0,1)){
		case 'A' : 
			title_info.Text = "차량선택"; 
			menuGrd2.SetCellProp("Head", 1, "text", "차량코드");
			menuGrd2.SetCellProp("Head", 2, "text", "사용유무");
			menuGrd2.SetCellProp("Head", 3, "text", "트럭상태");
			menuGrd2.SetColProp(1,"Width",100);
			menuGrd2.SetColProp(2,"Width",200);
			menuGrd2.SetColProp(3,"Width",200);
			break;
		case 'B' : title_info.Text = "거래처선택"; break;
		case 'C' : 
			title_info.Text = "CODE선택";
			menuGrd2.SetCellProp("Head", 1, "text", "CODE");
			menuGrd2.SetCellProp("Head", 2, "text", "CODE명");
			menuGrd2.SetCellProp("Head", 3, "text", "CODE원명");
			menuGrd2.SetColProp(1,"Width",50);
			menuGrd2.SetColProp(2,"Width",200);
			menuGrd2.SetColProp(3,"Width",250);

		case 'F' : title_info.Text = "부서선택"; break;
		case 'G' : title_info.Text = "설비선택"; break;
		case 'H' : title_info.Text = "사원선택"; break;

		default : title_info.Text = "";
	}
	imgQuery_OnClick();
}

/********************************************************************** 
 * 개 발 자 : 이상건
 * 내용설명 : 선택별 조회
 * 주요변수 : 
 **********************************************************************/ 
function imgQuery_OnClick(obj,nX,nY)
{
	var iSel = SEL;
	var iSelLeft = substr(iSel,0,1);
	var iSelRight = substr(iSel,1,1);

	if (iSelLeft== 'A'){   
		http.Sync 	= true;
		var sKind		= "getFSA120PF";		
		var sMethodName = "service::system/getFSA120PF";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(COMP);
			sArgument  += " TRUCD=" + quote(ed_select.Text);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSelLeft== 'B'){ 
		if (iSelRight=="3"){
			http.Sync 	= true;
			var sKind		= "getMIZ221PF";		
			var sMethodName = "service::system/getMIZ221PF";  					
			var sInDataSet  = "";
			var sOutDataSet = "ds_Select=ds_Select";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				//sArgument  += " COMPS=" + quote(COMP);
				sArgument  += " VNDNR=" + quote(ed_select.Text);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		}
		if (iSelRight=="4"){
			http.Sync 	= true;
			var sKind		= "getMIZ220PF";		
			var sMethodName = "service::system/getMIZ220PF";  					
			var sInDataSet  = "";
			var sOutDataSet = "ds_Select=ds_Select";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				//sArgument  += " COMPS=" + quote(COMP);
				sArgument  += " CATNO=" + quote(ed_select.Text);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		}
	}

	if (iSel== 2){ 
		http.Sync 	= false;
		var sKind		= "getMIZ221PF";		
		var sMethodName = "service::system/getMIZ221PF";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " VNDNR=" + quote(ed_select.Text); //구매업체코드
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
}

/********************************************************************** 
 * 개 발 자 : 이상건
 * 개발일자 : 2011-08-25
 * 내용설명 : 선택
 * 주요변수 : 
 **********************************************************************/ 
function imgSelect_OnClick(obj,nX,nY)
{
	var arrReturn  =  Array();
	arrReturn[0]  = ds_Select.GetColumn(ds_Select.row,"A1");
	arrReturn[1]  = ds_Select.GetColumn(ds_Select.row,"A2");
	arrReturn[2]  = ds_Select.GetColumn(ds_Select.row,"A3");
	close(arrReturn);
}

function menuGrd2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	imgSelect_OnClick();
}
/********************************************************************** 
 * 개 발 자 : 이상건
 * 개발일자 : 2011-08-25
 * 내용설명 : close
 * 주요변수 : 
 **********************************************************************/ 
function imgClose_OnClick(obj,nX,nY)
{
	Close();
}
