Window.Script:
var rCnt = 0;
var rRtn = false;
function Button0_OnClick(obj)
{
	rCnt = 0;
	rRtn = false;
	setTimer(1,1);  //1000는 1초를 의미합니다.  더빨리 실행하려면 숫자를 줄이세요 
}

function Button1_OnClick(obj)
{
trace("rRtn Set");
	rRtn = true;
}

function form_OnTimer(obj,nEventID)
{
	if((rCnt == 5000) || rRtn == true)
	{
		KillTimer(1);
	} else {
		rCnt ++;
		trace(rRtn + "===" + rCnt);
	}	

}
