/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getFSAZ151(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getFSAZ151";						
	var sMethodName = "service::shipment/getFSAZ151List";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FSAZ151=ds_FSAZ151";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@SHPDT=" + quote(cal_SHPDT.Value);
		sArgument += "@DAYCD=" + quote(getDay(cal_SHPDT.Value)+1);
	var str = dialog("Common::PGMLOAD_SHIP.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}
/********************************************************************** 
 * 내용설명 : 화면저장
 **********************************************************************/ 
function setFSAZ151(){

	if(ds_FSAZ151.GetRowType(ds_FSAZ151.currow)=="update" ||ds_FSAZ151.GetRowType(ds_FSAZ151.currow)=="insert" ){
		if(!ds_FSAZ151_CanRowPosChange()){
			return;
		}
	} 

	var sKind		= "setFSAZ151";						
	var sMethodName = "service::shipment/setFSAZ151List";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FSAZ151=ds_FSAZ151:U";     //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "ds_FSAZ151_CHECK=ds_FSAZ151_CHECK";     //자료 저장 시 보낼 DataSet 명칭
	var sArgument   = "";
		//sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " SUSER=" + quote(userId); //사용자
		sArgument += " TRAGB=" + quote("M"); // M:이동품   O:OEM출하
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FSAZ151_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FSAZ151_CHECK.GetRowCount();i++){
			alert("출하지시번호 : " +ds_FSAZ151_CHECK.GetColumn(i,"TRKNO") + " = " + ds_FSAZ151_CHECK.GetColumn(i,"RTMSG"));
		}
		getFSAZ151();  //error항목이 존재시에는 재조회
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){

	var result = true;
	cal_SHPDT.BKColor="white";

	if (cal_SHPDT.Value == ""){
		cal_SHPDT.BKColor="paleturquoise";
		alert("[확인]이동지시일자를 확인바랍니다.");//[확인] 기준일자를 확인바랍니다.
		//alert(setPopMessage("4716"));//[확인] 기준일자를 확인바랍니다.
		setMessage("4716");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "이동지시정의";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|이동지시정의";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

