function fn_Delete()
{
	if(ds_FSAZ151.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FSAZ151.DeleteRow(ds_FSAZ151.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

