function ds_FSAZ151_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;
	var p_GATCD="";
	var p_TRACD="";
	var p_CHASU="";
	if(ds_FSAZ151.GetRowType(ds_FSAZ151.currow)=="update" ||ds_FSAZ151.GetRowType(ds_FSAZ151.currow)=="insert" ){
		p_GATCD=ds_FSAZ151.GetColumn(ds_FSAZ151.currow,"GATCD");
		p_TRACD=ds_FSAZ151.GetColumn(ds_FSAZ151.currow,"TRACD");
		p_CHASU=ds_FSAZ151.GetColumn(ds_FSAZ151.currow,"CHASU");

		if(length(p_GATCD)!=0 && length(p_TRACD)!=0 && length(p_CHASU)!=0){
			var sKind		= "getFSAZ150View";						
			var sMethodName = "service::shipment/getFSAZ150ListView";     // 서버에서 호출 될 Method 명
			var sInDataSet  = "";     //자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet  = "ds_FSAZ150View=ds_FSAZ150View";     //자료 저장 시 보낼 DataSet 명칭
			var sArgument   = "";
				//sArgument += " LIB1=" + quote(Lib1);
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " DAYCD=" + quote(getDay(cal_SHPDT.Value)+1);
				sArgument += " GATCD=" + quote(p_GATCD);
				sArgument += " TRACD=" + quote(p_TRACD);
				sArgument += " CHASU=" + quote(p_CHASU);
				sArgument += " SHPDT=" + quote(cal_SHPDT.Value);
			http.Sync=true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync=false;

			if(ds_FSAZ150View.rowcount>0){
				ds_FSAZ151.SetColumn(ds_FSAZ151.currow,"TIMES",ds_FSAZ150View.GetColumn(0,"TIMES"));
				ds_FSAZ151.SetColumn(ds_FSAZ151.currow,"TIMEE",ds_FSAZ150View.GetColumn(0,"TIMEE"));
				ds_FSAZ151.SetColumn(ds_FSAZ151.currow,"FTIMS",ds_FSAZ150View.GetColumn(0,"FTIMS"));
				ds_FSAZ151.SetColumn(ds_FSAZ151.currow,"FTIME",ds_FSAZ150View.GetColumn(0,"FTIME"));
			}else{
				alert("[알림]등록된 계획이 없습니다.");
				//alert(setPopMessage("1001"));
				//setMessage("1001");
				return false;
			}
		}
		return true;
	}
	http.usewaitcursor = true;
}
