mapper.select:
SELECT *
FROM (
     SELECT ROWNUM AS RN, A.*
      FROM (




      ) A
      ORDER BY A.ID
 ) AA


  WHERE RN BETWEEN ((${pageable.pageNumber} - 1) * ${pageable.pageSize}) + 1 AND ${pageable.offset}

ORDER BY RN

SELECT COUNT(ID) AS CNT
FROM (




 ) AA

mapper.insert:
    INSERT INTO TB_BA_OFFICE_DETAIL (
		ID
		,MAKER
		,OFFICE_CD
		,SEQ
		,MECHANIC
		,MECHANIC_TEL
		,REPR_YN
		,AUTOINSERT
		,GROUP_ID
		,OFFICE_DIV
		,PART_DIV
		,RGSTR_ID
		,RGST_DT
		,UPDTR_ID
		,UPDT_DT

    ) VALUES (
		#{id}
		,#{maker}
		,#{officeCd}
		,#{seq}
		,#{mechanic}
		,#{mechanicTel}
		,#{reprYn}
		,#{autoinsert}
		,#{groupId}
		,#{officeDiv}
		,#{partDiv}
		,#{rgstrId}
		,#{rgstDt}
		,#{updtrId}
		,#{updtDt}

    )


      SELECT SEQ_TB_BA_OFFICE_DETAIL.NEXTVAL FROM DUAL

mapper.update:
    UPDATE TB_BA_OFFICE_DETAIL SET
		MAKER = #{maker}
		,OFFICE_CD = #{officeCd}
		,SEQ = #{seq}
		,MECHANIC = #{mechanic}
		,MECHANIC_TEL = #{mechanicTel}
		,REPR_YN = #{reprYn}
		,AUTOINSERT = #{autoinsert}
		,GROUP_ID = #{groupId}
		,OFFICE_DIV = #{officeDiv}
		,PART_DIV = #{partDiv}
		,UPDTR_ID = #{updtrId}
		,UPDT_DT = #{updtDt}

    WHERE ID = #{id}
