function form_OnLoadCompleted(obj)
{    
    var sArgument_new ="";

    for(i=0;i<Length(sArgument);i++){
		if(Substr(sArgument, i, 1) = "@"){
			sArgument_new += " ";			
		} else {
			sArgument_new += Substr(sArgument, i, 1);						
		}
    }

	SetWaitCursor(true); 	
	http.Sync = false;	
	Transaction(sKind, sMethodName, "", sOutDataSet, sArgument_new, "callback");
	SetTimer(1,100);
}

