Window.Script:
#include "script::lib_script_common.js";

function form_OnLoadCompleted(obj)
{
	SetWaitCursor(true); 

	http.Sync = false;

	SetTimer(1,1000);

}

function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
		close();

	}else{
		setMessage("1004");	
		close();
	}
}

function form_OnTimer(obj,nEventID)
{
	KillTimer(1);

	// 메뉴로그 저장
	global.FrameBottom.saveMenuLog(G_ds_user.GetColumn(0,"USER_ID"),mendId);			

	web01.Run();
	close();
}
