Window.Script:
function grd_reservation_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var confirm_count = ds_contract.GetColumn(ds_contract.row, "CONFIRM_COUNT");//심의 요청 여부
	if(confirm_count==0){
		alert(setPopMessage("2006"));	// 20110103l-koowgnojeel, 2006 == 광고물관리에 등록된 계약건 입니다. 
		return;
	}

	parent.advertisementEdit.edt_adCust.text = ds_contract.GetColumn(ds_contract.row, "ORG_CUST_NM");
	parent.advertisementEdit.edt_reservNo.text = ds_contract.GetColumn(ds_contract.row, "REGIST_NO");

	//계약 detail을 조회해서 출력
	var regist_no = ds_contract.GetColumn(ds_contract.row, "REGIST_NO");	
	getContractDtlList(regist_no);
}

function contractPop_OnLoadCompleted(obj)
{
	getContractList();
}


//계약목록 조회
function getContractList()
{	
	var param = "";

	Transaction(
		"getContractList",								// Transaction ID
		"service::advertisement/contractList.do",		// service
		"",												// input Dataset
		"ds_contract=ds_contract",						// output Dataset
		param,											// parameter
		"callBack"										// callback
	);
}

//계약목록상세 조회 
function getContractDtlList(regist_no)
{	
	var param = "";
    param = " REGIST_NO=" + quote(regist_no);

	Transaction(
		"getContractDtlList",							// Transaction ID
		"service::advertisement/contractDtlList.do",	// service
		"",												// input Dataset
		"ds_contractDtl=ds_contractDtl",				// output Dataset
		param,											// parameter
		"callBack"										// callback
	);
}

//CallBack
function callBack(svcId, rtnCd, rtnMsg){
	if(rtnCd<0){
		alert(rtnMsg);
		return;
	}	
	if(svcId == "getContractDtlList")
	{
		ds_reservationSel.Copy(ds_contractDtl);
		close();
	}

}
