Window.Script:
function Image22_OnClick(obj,nX,nY)
{
	global.FrameTop2.visible = true;
	global.FrameTopClose.visible = false;
}

function Button8_OnClick(obj)
{
	if (G_ScreenSize=="N"){
		//trace("1");
		//전체열기
		global.FrameTop2.visible = false;
		global.FrameTopClose.visible = true;
		global.FrameLeft.visible = false;
		global.FrameLeftClose.visible = true;

		G_ScreenSize = "Y";
	}else{
		//trace("2");
		//전체닫기

		global.FrameTop2.visible = true;
		global.FrameTopClose.visible = false;
		global.FrameLeft.visible = true;
		global.FrameLeftClose.visible = false;

		G_ScreenSize = "N";
	}
}
