/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDWH(){

	if(length(trim(ca_START.Value)) < 6){
		alert(setPopMessage("1504"));
		setMessage("1504");
		ca_START.SetFocus();
		return;
	}

	if(length(trim(ca_END.Value)) < 6){
		alert(setPopMessage("1505"));
		setMessage("1505");
		ca_END.SetFocus();
		return;
	}
	var STARTDATE = toNumber(substr(ca_START.Value,0,6));
	var ENDDATE = toNumber(substr(ca_END.Value,0,6));

	ds_DWH.ClearData();
	//Paging처리
	var sKind		= "getDWH";						
	var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CNT=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(iif(cb_searchCodeName1.Value == "PE","E1","C1"));
		sArgument += " STARTDATE=" + quote(STARTDATE);
		sArgument += " ENDDATE=" + quote(ENDDATE);
		sArgument += " PAYCD=" + quote(cmb_GUBCD.Value);
		sArgument += " SQLID=" + "selectdwhz008qCnt";
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	var dsCnt=0;
	var rowCnt=0;
		dsCnt =parseInt(ds_CNT.GetColumn(0,"CNT"));
	if(dsCnt>0){
		rowCnt=ceil(dsCnt/5000);
	}
	var pCnt_F=0;
	var pCnt_T=0;

	for(i=0; i<rowCnt; i++){
		if( i==0){
			pCnt_F =1;
			pCnt_T =5000; 
		}else{
			pCnt_F = (i*5000)+1; 
			pCnt_T = (i+1)*5000; 
		}

			var sKind		= "getDWH";						
			var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_DWH_SUB=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " LIB3=" + quote(Lib3);
			sArgument += " COMPS=" + quote(iif(cb_searchCodeName1.Value == "PE","E1","C1"));
			sArgument += " SQLID=" + "selectdwhz008q";	
			sArgument += " STARTDATE=" + quote(STARTDATE);
			sArgument += " ENDDATE=" + quote(ENDDATE);
			sArgument += " STARTNUM=" + quote(pCnt_F);
			sArgument += " ENDNUM=" +  quote(pCnt_T);
			sArgument += " PAYCD=" + quote(cmb_GUBCD.Value);

			//var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
			/*
			if (str == 'T'){  //정상조회
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}else if (str == 'B'){   //data값이 없을때
				alert(setPopMessage("1003"));
				setMessage("1003");	
			}else if (str == 'E'){   //error발생시
				alert(setPopMessage("1002"));
				setMessage("1002");	
			}
			*/
			ds_DWH.AppendDataset(ds_DWH_SUB);			
			ds_DWH_SUB.Clear();
	}

}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "월별_소급분지급내역리스트";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월별_소급분지급내역리스트";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getDWHZ008Q"){
		// 조회건수가 없을경우
		if(ds_dwh.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 9) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

