function getComCode(){
	//setCompanyName(ds_ComCode_PLNT);
	//cb_searchCodeName1.Value=company;

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	//cb_searchCodeName1.Value = comps;
	//ds_ComCode_PLNT.InsertRow(0);
	//ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	//ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_searchCodeName1.Index = 0;

}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDWH(){

	if(cb_searchCodeName1.Value =="C1"){
		Lib3="PSCDBLIB";
	}else if(cb_searchCodeName1.Value =="H1"){
		Lib3="PSHDBLIB";
	}else if(cb_searchCodeName1.Value =="S1"){
		Lib3="PSSDBLIB";
	}

	if(length(trim(ca_START.Value)) < 8){
		alert(setPopMessage("4674"));
		setMessage("4674");
		ca_START.SetFocus();
		return;
	}
	if(length(trim(ca_END.Value)) < 8){
		alert(setPopMessage("4674"));
		setMessage("4674");
		ca_END.SetFocus();
		return;
	}
	var yyyySTART = toNumber(substr(ca_START.Value,0,8));
	var yyyyEND = toNumber(substr(ca_END.Value,0,8));

	var sKind		= "getDWH";						
	var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_DWH=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@LIB3=" + quote(Lib3);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@SQLID=" + "selectdwhz047q";	
	sArgument += "@STARTDATE=" + quote(yyyySTART);	
	sArgument += "@ENDDATE=" + quote(yyyyEND);
	var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "국내 구매단가 정단가화 리스트";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|국내 구매단가 정단가화 리스트";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getDWH"){
		// 조회건수가 없을경우
		if(ds_dwh.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 9) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

