 **********************************************************************/ 
function getDesign(){
	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
	/*
	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE2","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	ComboBoxPlant.Index = 0;
	*/
}
function getDWH000(){
	http.Sync 	= true;
	var sKind		= "getDWHZ000Q";						
	var sMethodName = "service::dwhlib/selectDWHZ000Q.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_DWHZ000Q=ds_DWHZ000Q";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " JOBFL=" + quote(jobpfnm);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	ca_RVSDT.Value = ds_DWHZ000Q.GetColumn(0,"JYYMM")+"01";
}

 /********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDWHZ103Q(){

	ds_DWH.Clear();

	if(length(trim(ca_RVSDT.Value)) < 8){
		alert(setPopMessage("2016"));  //[알림] 날짜를 입력하세요.
		setMessage("2016");	
		ca_RVSDT.SetFocus();
		return;
	}

	//Paging처리
	var sKind		= "getDWH";						
	var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CNT=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " SQLID=" + "selectDWHZ103QCnt";
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	var dsCnt=0;
	var rowCnt=0;
		dsCnt =parseInt(ds_CNT.GetColumn(0,"CNT"));
	if(dsCnt>0){
		rowCnt=ceil(dsCnt/10000);
	}
	var pCnt_F=0;
	var pCnt_T=0;

	for(i=0; i<rowCnt; i++){
		if( i==0){
			pCnt_F =1;
			pCnt_T =10000; 
		}else{
			pCnt_F = (i*10000)+1; 
			pCnt_T = (i+1)*10000; 
		}

			var sKind		= "getDWH";						
			var sMethodName = "service::dwhlib/selectManagement.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_DWH_SUB=ds_DWH";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";

			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " SQLID=" + "selectDWHZ103Q";	
			sArgument += " STARTNUM=" + quote(pCnt_F);
			sArgument += " ENDNUM=" +  quote(pCnt_T);

			//var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
			http.Sync = true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;

			ds_DWH.AppendDataset(ds_DWH_SUB);			
			ds_DWH_SUB.Clear();
	}
	//ds_DWH.Copy(ds_DWH_MERGE);
	/*
	if(ds_DWH.rowcount <= 0){
		alert(setPopMessage("1003"));
		setMessage("1003");
	}else{
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}
	*/
}

