Window.Script:
#include "script::lib_script_common.js";

function form_OnLoadCompleted(obj)
{
	http.Sync = true;
	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",parent.exeTime);
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);
	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		//var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall("PSEOBJLIB","BHZZ100CP#","PS201008",103);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(parent.pgmLib, parent.pgmName, parent.param, pgmSize);

		//정상적으로 수행했을 시
		if(rtn2 != 0){
			//리턴 파라메타 값을 출력한다.
			alert("성공=======>" + WTA_RemoteCall0.RC_GetReturnParm(1));
		}else{
			//오류 코드 경고창
			alert("에러 메세지=======>" + WTA_RemoteCall0.bstrErrorMessage);
		}
	//접속실패시
	}else{
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + WTA_RemoteCall0.ulErrorCode);
	}
	//연결종료
	WTA_RemoteCall0.RC_Disconnect();
	close();
}
