function onLoadCompleted(obj)
{
	getTitle();

	if(!isAuth){

		http.Sync 	= true;
		var sKind		= "getComCode";						
		var sMethodName = "service::login/selectComCode.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_ComCode_COMP=ds_ComCode_COMP";
		var sArgument   = "";
			sArgument   += " CLASS='COMP'";
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	

		ds_ComCode_COMP.Filter("CODE1=='" + G_ds_user.GetColumn(0,"COMPANY") + "'");
	}

	ed_yyyymm.text = substr(nowDate,0,6);
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 실행
 * 주요변수 : 
 **********************************************************************/ 
function Button0_OnClick(obj)
{
		http.Sync 		= true;
		var sKind		= "testProc";						
		var sMethodName = "service::sample/testProc.do";     			// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "";    // 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " COMPS1=" + quote("E1");
		sArgument += " JOBDT1=" + quote("20110222");
		sArgument += " PROCS1=" + quote("C-06");
		sArgument += " daygu1=" + quote("D");

		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}



