#include "script::lib_script_common.js";

var Lib = G_ds_user.GetColumn(0,"LIB2");
var CigmaLib = G_ds_user.GetColumn(0,"LIB1");

function on_LoadCompleted(obj){	
	getTitle();	
	getComCode();
	search_prtym = substr(today(), 0,4);	
}

function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");	
	http.Sync 	= false;
}

function getChartList(){
	var sKind		= "getChartList";						
	var sMethodName = "service::chart/selectChart05.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_chart=ds_chart";    // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";	
	//sArgument += " COMPS=" + quote(search_comps.Value);
	sArgument += " PRTYM=" + quote(search_prtym.Value);
	sArgument += " PROCS_FROM=" + quote(search_procsFrom.Text);	
	sArgument += " PROCS_TO=" + quote(search_procsTo.Text);	

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

function loadingChart0(){	
	xChart0.Titles[0].Text = "월 제품별 생산실적(생산계획 꺽은선)";

	xChart0.OpenData("COD_Values", 2, 12);

	for(var i=0;i<12;i++){
		currentSilqy = "";
		currentPrtqy = "";		
		for(var j=0;j<ds_chart.rowcount();j++){			
			var month = ds_chart.GetColumn(j, "MONTH");						
			if(i+1 == parseInt(month)){
				currentSilqy = ds_chart.GetColumn(j, "SIL_PRICE");
				currentPrtqy = ds_chart.GetColumn(j, "PRT_PRICE");				
				break;
			}
		}		

		xChart0.Value(0, i) = nvl(currentSilqy, "0");
		xChart0.Value(1, i) = nvl(currentPrtqy, "0");	
	}

	xChart0.CloseData("COD_Values");

	xChart0.Legend(0) = "1월";
	xChart0.Legend(1) = "2월";
	xChart0.Legend(2) = "3월";
	xChart0.Legend(3) = "4월";
	xChart0.Legend(4) = "5월";
	xChart0.Legend(5) = "6월";
	xChart0.Legend(6) = "7월";
	xChart0.Legend(7) = "8월";
	xChart0.Legend(8) = "9월";
	xChart0.Legend(9) = "10월";
	xChart0.Legend(10) = "11월";
	xChart0.Legend(11) = "12월";

	xChart0.Series[0].Gallery = "Gallery_Bar";
	xChart0.Series[1].Gallery = "Gallery_Lines";

	xChart0.AxisX.Gridlines = True;
	xChart0.AxisY.Gridlines = True;

	xChart0.AxisY.LabelsFormat.CustomFormat = "#,###"; // 출력될 라벨 포멧	
}

function loadingChart1(){	
	xChart1.Titles[0].Text = "월 제품별 생산실적 달성률";

	xChart1.OpenData("COD_Values", 1, 12);

	for(var i=0;i<12;i++){
		currentPer = "";		
		for(var j=0;j<ds_chart.rowcount();j++){			
			var month = ds_chart.GetColumn(j, "MONTH");						
			if(i+1 == parseInt(month)){
				currentPer = ds_chart.GetColumn(j, "PER");
				break;
			}
		}		
		xChart1.Value(0, i) = nvl(currentPer, "0");						
	}

	xChart1.CloseData("COD_Values");

	xChart1.Legend(0) = "1월";
	xChart1.Legend(1) = "2월";
	xChart1.Legend(2) = "3월";
	xChart1.Legend(3) = "4월";
	xChart1.Legend(4) = "5월";
	xChart1.Legend(5) = "6월";
	xChart1.Legend(6) = "7월";
	xChart1.Legend(7) = "8월";
	xChart1.Legend(8) = "9월";
	xChart1.Legend(9) = "10월";
	xChart1.Legend(10) = "11월";
	xChart1.Legend(11) = "12월";

	xChart1.AxisX.Gridlines = True;
	xChart1.AxisY.Gridlines = True;

	xChart1.AxisY.LabelsFormat.CustomFormat = "#,###"; // 출력될 라벨 포멧	
}

function nvl(val1, val2){
	if(length(val1)==0){
		return val2;
	}
	return val1;
}

function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getChartList"){
		loadingChart0();
		loadingChart1();
		// 조회건수가 없을경우
		if(ds_menuList.rowcount <= 0){	
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}		
	// tran 성공	
	}
}








