function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");

/*
	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_comp.index = 0;	
*/	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 조회
 * 주요변수 : 
 **********************************************************************/ 
function getPpmi004m(){
	http.Sync = true;
	var sKind		= "getPpmi004m";						
	var sMethodName = "service::sample/selectPpmi004M.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi004m=ds_ppmi004m";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	if(length(trim(ed_itnbr.Value)) == 0){
		alert(setPopMessage("2017"));	// 20110103l-koowgnojeel, 2017 == 품번을 입력하세요.
		ed_itnbr.SetFocus();
		return;
	}

	sArgument += " LIB=" + quote(Lib);
	sArgument += " CigmaLIB=" + quote(CigmaLib);	
	sArgument += " PINBR=" + quote(ed_itnbr.Value);
	sArgument += " STEP='1'";
	sArgument += " STEP_TXT='1'";

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 데이터 APPEND
 * 주요변수 : 
 **********************************************************************/ 

function subPpmi004m(dsObj)
{
	var intStep2 = 0;

	strStep = strStep + (intStep+1);



	intStep++;

	ds_tmp2.Copy(object(dsObj));
	ds_tmp.ClearData();

	for(i=0;i<ds_tmp2.rowcount;i++)
	{
		var sArgument   = "";

		intStep2++;
		http.Sync = true;
		sArgument += " CigmaLIB=" + quote(CigmaLib);	
		sArgument += " PINBR=" + quote(ds_tmp2.GetColumn(i,"ITNBR"));
		sArgument += " STEP=" + quote(ds_tmp2.GetColumn(i,"ROWNUMBER"));
		sArgument += " STEP2=" + quote(intStep2);
		sArgument += " STEP_TXT=" + quote(makePoint(intStep) + intStep);



		Transaction("subPpmi004m", "service::sample/selectPpmi004MSub.do", "", "ds_ppmi004mSub=ds_ppmi004mSub", sArgument, "");

		ds_main.AppendDataset("ds_ppmi004mSub");
		ds_tmp.AppendDataset("ds_ppmi004mSub");

	}

	if(object(dsObj).rowcount != 0)
	{
		subPpmi004m("ds_tmp");
	} else {
		ds_main.Sort("ROWNUMBER");
		alert(setPopMessage("1004"));
		setMessage("1004");			

	}
}


function makePoint(cnt)
{
	var returnCnt = cnt -1;
	var returnValue = "";

	for(i=0; i<returnCnt;i++)
	{
		returnValue += ".";
	}

	return returnValue;
}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 

function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getPpmi004m"){
		// 조회건수가 없을경우
		if(ds_ppmi004m.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			ds_main.Copy("ds_ppmi004m");
			//alert(ds_main.rowcount);
			subPpmi004m("ds_ppmi004m");

		}
	// tran 성공	
	}else if(svcId=="setPpmi007m"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}


function setExcel()
{
	var fName   = "BOM 조회(정방향)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|BOM 조회(정방향)";

	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


