#include "script::lib_script_common.js";

var Lib = G_ds_user.GetColumn(0,"LIB2");
var nowDate = today();
var procs = "" ;
var ordat = "" ;

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	ed_syyyymmdd.Value = nowDate;	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");


	http.Sync 	= true;
	var sKind		= "getComCode2";						
	var sMethodName = "service::sample/selectPpmi001MCode.do"; 	
	var sInDataSet  = "";
	var sOutDataSet = "ds_ppmi001mCode=ds_ppmi001mCode";
	var sArgument   = "";
		sArgument   += " LIB=" + quote(Lib);
		sArgument   += " COMPS=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_ppmi001mCode.InsertRow(0);
	ds_ppmi001mCode.SetColumn(0,"ROWGB","");
	ds_ppmi001mCode.SetColumn(0,"ROWNM","[전체]");
	Combo0.index = 0;	

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-08
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getPpmi006mTab1(){
	http.Sync = true;
	var sKind		= "getPpmi006mTab1";						
	var sMethodName = "service::sample/selectPpmi006MTab1.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi006mTab1=ds_ppmi006mTab1";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";


	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(G_ds_user.GetColumn(0,"PLANT_CD"));
	sArgument += " PROCS=" + quote(procs);	
	sArgument += " ORDAT=" + quote(ordat);	

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");


	if(ds_ppmi006mTab1.rowcount <= 0) return ;

	http.Sync = true;
	var sKind		= "getPpmi006mTab1Sub";						
	var sMethodName = "service::sample/selectPpmi006MTab1Sub.do";     			// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             							// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi006mTab1Sub=ds_ppmi006mTab1Sub";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(ds_ppmi006mTab1.GetColumn(ds_ppmi006mTab1.row,"COMPS"));
	sArgument += " PROCS=" + quote(ds_ppmi006mTab1.GetColumn(ds_ppmi006mTab1.row,"PROCS"));	
	sArgument += " ORDAT=" + quote(ds_ppmi006mTab1.GetColumn(ds_ppmi006mTab1.row,"ORDAT"));	
	sArgument += " DAYGU=" + quote(ds_ppmi006mTab1.GetColumn(ds_ppmi006mTab1.row,"DAYGU"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-08
 * 내용설명 : 화면저장
 * 주요변수 : 
 **********************************************************************/ 
function setPpmi006mTab1(){
	if(!validationChk()) return;
	var sKind		= "setPpmi006mTab1";						
	var sMethodName = "service::sample/savePpmi006MTab1.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ppmi006mTab1=ds_ppmi006mTab1:U";     		//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

}



/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : validation 체크
 * 주요변수 : 
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<ds_ppmi006mTab1.rowcount(); i++){
		if(length(trim(ds_ppmi006mTab1.GetColumn(i, "DAYGU")))==0){
			alert(setPopMessage("2027"));	// 20110103l-koowgnojeel, 2027 == 주야구분을 선택하세요.
			ds_ppmi006mTab1.row = i;
			return false;
		}
		if(length(trim(ds_ppmi006mTab1.GetColumn(i, "JOBSQ")))==0){
			alert(setPopMessage("2019"));	// 20110103l-koowgnojeel, 2019 == 작업 순서를 입력하세요.
			ds_ppmi006mTab1.row = i;
			return false;
		}
		if(length(trim(ds_ppmi006mTab1.GetColumn(i, "PRTNO")))==0){
			alert(setPopMessage("2017"));	// 20110103l-koowgnojeel, 2017 == 품번을 입력하세요.
			ds_ppmi006mTab1.row = i;
			return false;
		}	
		if(length(trim(ds_ppmi006mTab1.GetColumn(i, "CYCTM")))==0){
			alert(setPopMessage("2050"));	// 20110103l-koowgnojeel, 2050 == 텍트타임 임력하세요.
			ds_ppmi006mTab1.row = i;
			return false;
		}
		if(length(trim(ds_ppmi006mTab1.GetColumn(i, "ORQTY")))==0){
			alert(setPopMessage("2021"));	// 20110103l-koowgnojeel, 2021 == 지시수량을 입력하세요.
			ds_ppmi006mTab1.row = i;
			return false;
		}
		if(length(trim(ds_ppmi006mTab1.GetColumn(i, "SEQNO")))==0){
			alert(setPopMessage("2022"));	// 20110103l-koowgnojeel, 2022 == SEQNO를 입력하세요.
			ds_ppmi006mTab1.row = i;
			return false;
		}						
	}

	return true;
}




function onRowPosChange(obj,nOldRow,nRow)
{
	if(ds_ppmi006mTab1.GetRowType(ds_ppmi006mTab1.row) == "insert"){
		cd_daygu.Enable = true;
		Edit2.Enable = true;
		Edit1.Enable = true;	
	} else {
		cd_daygu.Enable = false;
		Edit2.Enable = false;
		Edit1.Enable = false;	
	}

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-06
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = ed_syyyymmdd.Value + "-전광판 생산현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|전광판 생산현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getPpmi006mTab1"){
		// 조회건수가 없을경우
		if(ds_ppmi006mTab1.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}else if(svcId=="setPpmi006mTab1"){
		alert(setPopMessage("1001"));
		setMessage("1001");		
	}
}

