function fn_Input()
{
	ds_ppmi006mTab1.AddColumn("COMPS","STRING",255);
	ds_ppmi006mTab1.AddColumn("ORDAT","STRING",255);
	ds_ppmi006mTab1.AddColumn("PROCS","STRING",255);
	ds_ppmi006mTab1.AddColumn("SPLIT","STRING",255);
	ds_ppmi006mTab1.AddColumn("SEQNO","STRING",255);
	ds_ppmi006mTab1.AddColumn("SERNO","STRING",255);
	ds_ppmi006mTab1.AddColumn("DAYGU","STRING",255);
	ds_ppmi006mTab1.AddColumn("JOBSQ","STRING",255);
	ds_ppmi006mTab1.AddColumn("PRTNO","STRING",255);
	ds_ppmi006mTab1.AddColumn("PRLOT","STRING",255);
	ds_ppmi006mTab1.AddColumn("QTUNT","STRING",255);
	ds_ppmi006mTab1.AddColumn("ORQTY","STRING",255);
	ds_ppmi006mTab1.AddColumn("CYCTM","STRING",255);

	if(checkInsertCount("ds_ppmi006mTab1")== false) return;	

	ds_ppmi006mTab1.AddRow();

	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"COMPS",G_ds_user.GetColumn(0,"PLANT_CD"));
	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"ORDAT",ordat);
	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"PROCS",procs);
	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"SPLIT","1");
	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"SERNO","1");
	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"PRLOT","1");
	ds_ppmi006mTab1.SetColumn(ds_ppmi006mTab1.row,"QTUNT","1");

}

function fn_Delete()
{
	ds_ppmi006mTab1.DeleteRow(ds_ppmi006mTab1.row);
}

