#include "script::lib_script_common.js";

var Lib = G_ds_user.GetColumn(0,"LIB2");
var nowDate = today();

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	ed_yyyymm.Value = substr(nowDate,0,6);

}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	ds_ComCode_PLNT.Filter("code2=='" + G_ds_user.GetColumn(0,"PLANT_CD") +"'");



/*
	ds_ComCode_PLNT.InsertRow(0);
	ds_ComCode_PLNT.SetColumn(0,"CODE1","");
	ds_ComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_comp.index = 0;	
*/	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 그룹 조회
 * 주요변수 : 
 **********************************************************************/ 
function getPpmi003mGroup(){
	var sKind		= "getPpmi003mGroup";						
	var sMethodName = "service::sample/selectPpmi003MGroup.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             						// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi003mGroup=ds_ppmi003mGroup";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " COMPS=" + quote(cb_comp.Value);
	sArgument += " PROCS=" + quote(ed_procs.Value);
	sArgument += " PRSNM=" + quote(ed_procsName.Value);

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 그룹 변경 이벤트
 * 주요변수 : 
 **********************************************************************/ 
function OnRowPosChanged(obj,nOldRow,nRow)
{
	http.Sync = true;
	var sKind		= "getPpmi010m";						
	var sMethodName = "service::sample/selectPpmi010M.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ppmi010m=ds_ppmi010m";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	sArgument += " PROCS=" + quote(ds_ppmi003mGroup.GetColumn(ds_ppmi003mGroup.row,"PROCS"));
	sArgument += " JOBMONTH=" + quote(substr(ed_yyyymm.Value,0,6));
	sArgument += " DAYGU=" + quote(cd_daygu.Value);		

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면 저장
 * 주요변수 : 
 **********************************************************************/ 
function setPpmi010m()
{
	var sKind		= "setPpmi010m";						
	var sMethodName = "service::sample/savePpmi010M.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ppmi010m=ds_ppmi010m:U";     		//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB=" + quote(Lib);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getPpmi010m"){
		// 조회건수가 없을경우
		if(ds_ppmi003m.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="setPpmi010m"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}



function On_Change(obj,strOldText,strNewText)
{
	OnRowPosChanged("","","");
}



 /*--------------------------------------------------------------
 * 아래 함수는 콩통함수 이므로 모든 화면에 필요
 * 함수명 변경 불가
 * 버튼 컨트롤 함수들
----------------------------------------------------------------*/
function fn_Favorite()
{
	alert("fn_Favorite");
}

function fn_Search()
{
	getPpmi003mGroup();
}

function fn_Input()
{
	if(checkInsertCount("ds_ppmi010m")== false) return;

	ds_ppmi010m.AddColumn("COMPS","STRING",255);	
	ds_ppmi010m.AddColumn("JOBDT","STRING",255);	
	ds_ppmi010m.AddColumn("CONTL","STRING",255);	
	ds_ppmi010m.AddColumn("JDTFM","STRING",255);	
	ds_ppmi010m.AddColumn("STRTM","STRING",255);	
	ds_ppmi010m.AddColumn("JDTTO","STRING",255);	
	ds_ppmi010m.AddColumn("ENDTM","STRING",255);	
	ds_ppmi010m.AddColumn("DAYGU","STRING",255);	
	ds_ppmi010m.AddColumn("PROCS","STRING",255);
	ds_ppmi010m.AddColumn("JOBTM","STRING",255);

	ds_ppmi010m.AddRow();

	ds_ppmi010m.SetColumn(ds_ppmi010m.row,"COMPS",ds_ppmi003mGroup.GetColumn(ds_ppmi003mGroup.row,"COMPS"));
	ds_ppmi010m.SetColumn(ds_ppmi010m.row,"PROCS",ds_ppmi003mGroup.GetColumn(ds_ppmi003mGroup.row,"PROCS"));
	ds_ppmi010m.SetColumn(ds_ppmi010m.row,"DAYGU",cd_daygu.Value);
	ds_ppmi010m.SetColumn(ds_ppmi010m.row,"JOBTM","0");

}

function fn_Delete()
{
	ds_ppmi010m.DeleteRow(ds_ppmi010m.row);
}

function fn_Save()
{
	setPpmi010m();
}

