function sendColumn(){
	Max5250.SendToColumn("COLUMN_1",ds_column.getColumn(0,"COLUMN_1"),1);
	Max5250.SendToColumn("COLUMN_2",ds_column.getColumn(0,"COLUMN_2"),1);
	Max5250.SendToColumn("COLUMN_3",ds_column.getColumn(0,"COLUMN_3"),1);
	Max5250.SendToColumn("COLUMN_4",ds_column.getColumn(0,"COLUMN_4"),1);
	Max5250.SendToColumn("COLUMN_5",ds_column.getColumn(0,"COLUMN_5"),1);
}
function sendField(){
	MAX5250.SendToField("FD03014",FD03014.Text );
	MAX5250.SendToField("FD03040",FD03040.Text );
	MAX5250.SendKeys("[Enter]");
}
function form_OnLoadCompleted(obj)
{
	var subStr = "";
	var sKind		= "CONVERTXMLDATA";	
	var sMethodName = "service::max5250/convertxmlData.do"; 
	var sInDataSet  = ""; 	
	var sOutDataSet = "ds_main=ds_main"; 	
	var mainStr = max5250.GetScreenData(1,24); 	
	var arg			= " str=" + quote(mainStr); 	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "fnc_callBack"); 	
}
