function btn_search()
{
	var subStr = "";
	var sKind		= "GETSUBFILELIST";						
	var sMethodName = "service::max5250/getSubFileList.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_save = ds_menu";                    // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_menu = ds_menu";    // 조회 후 돌려 받을 DataSet 명칭
	var arg			= " str=";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "callback");
}
//추가

function btn_add_OnClick(obj)
{
	if(ds_menu.rowcount == 0) ds_menu.AddRow();
	else ds_menu.InsertRow(0);

	var rtn  = Dialog("Max5250::screenInfoSave.xml", 545 , 370);
	if(rtn != 1) ds_menu.DeleteRow(0);
}
//저장
function btn_save_OnClick(obj)
{
	var subStr = "";
	var sKind		= "SETSUBFILE";						
	var sMethodName = "service::max5250/setSubFile.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_save = ds_menu:U";                    // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    // 조회 후 돌려 받을 DataSet 명칭
	var arg			= " str=";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "callback");
}

function btn_del_OnClick(obj)
{
	var subStr = "";
	var sKind		= "DELETESUBFILE";						
	var sMethodName = "service::max5250/deleteSubFile.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_save = ds_menu:D";                    // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    // 조회 후 돌려 받을 DataSet 명칭
	var arg			= " id="+quote(ds_menu.GetColumn(ds_menu.currow,"SCREEN_ID"));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "callback");
	ds_menu.DeleteRow(ds_menu.currow);
}


function Grid0_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var rtn  = Dialog("Max5250::screenInfoSave.xml", 545 , 370);
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){

		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="GETSUBFILELIST"){
		// 조회건수가 없을경우
		if(ds_menu.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	// tran 성공	
	}else if(svcId=="DELETESUBFILE" || svcId=="SETSUBFILE"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}







