/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getCJJZ501Q(){
	if(length(trim(ca_YYYY.Value)) < 8){
		alert(setPopMessage("4674"));
		setMessage("4674");
		ca_YYYY.SetFocus();
		return;
	}
	var yyyy0 = toNumber(substr(ca_YYYY.Value,0,4));
	var yyyy1 = yyyy0+1;
	var YM04 = substr(ca_YYYY.Value,0,4) + "04";
	var YM05 = substr(ca_YYYY.Value,0,4) + "05";
	var YM06 = substr(ca_YYYY.Value,0,4) + "06";
	var YM07 = substr(ca_YYYY.Value,0,4) + "07";
	var YM08 = substr(ca_YYYY.Value,0,4) + "08";
	var YM09 = substr(ca_YYYY.Value,0,4) + "09";
	var YM10 = substr(ca_YYYY.Value,0,4) + "10";
	var YM11 = substr(ca_YYYY.Value,0,4) + "11";
	var YM12 = substr(ca_YYYY.Value,0,4) + "12";
	var YM01 = yyyy1 + "01";
	var YM02 = yyyy1 + "02";
	var YM03 = yyyy1 + "03";

	var sKind		= "getCJJZ501Q";						
	var sMethodName = "service::pscstlib/selectCJJZ501Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CJJZ501Q=ds_CJJZ501Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(iif(cb_searchCodeName1.Value == "PE","E1","C1"));
	sArgument += "@PLNGB=" + quote(cmbSelect.Value);
	sArgument += "@PLNYM_F=" + quote(yyyy0+"04");
	sArgument += "@PLNYM_T=" + quote(yyyy1+"03");
	sArgument += "@YM04=" + quote(YM04);	
	sArgument += "@YM05=" + quote(YM05);
	sArgument += "@YM06=" + quote(YM06);
	sArgument += "@YM07=" + quote(YM07);
	sArgument += "@YM08=" + quote(YM08);
	sArgument += "@YM09=" + quote(YM09);
	sArgument += "@YM10=" + quote(YM10);
	sArgument += "@YM11=" + quote(YM11);
	sArgument += "@YM12=" + quote(YM12);
	sArgument += "@YM01=" + quote(YM01);
	sArgument += "@YM02=" + quote(YM02);
	sArgument += "@YM03=" + quote(YM03);
	var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "년판매계획대 재료비현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|년판매계획대 재료비현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 9) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

