/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getCYJC101Q(){
	if(length(trim(ca_YYYY.Value)) < 4){
		alert(setPopMessage("4674"));
		setMessage("4674");
		ca_YYYY.SetFocus();
		return;
	}

	var sKind		= "getEM006PR";						
	var sMethodName = "service::pscomlib/selectEM006PR.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_EM006PR=ds_EM006PR";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " CURYM=" + quote(nowDate);
	http.Sync=true;
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	var UD_rate = "0";
	var JY_rate = "0";
	var PD_rate = "0";
	var EU_rate = "0";
	var DM_rate = "0";
	for(var i=0;i<ds_EM006PR.rowcount;i++){
		if(ds_EM006PR.GetColumn(i,"CURC2") == "UD"){
			UD_rate = ds_EM006PR.GetColumn(i,"ERATE");
			st_UD.Text = "\ "+UD_rate;
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "JY"){
			JY_rate = ds_EM006PR.GetColumn(i,"ERATE");
			st_JY.Text = "\ "+JY_rate;
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "PD"){
			PD_rate = ds_EM006PR.GetColumn(i,"ERATE");
			st_PD.Text = "\ "+PD_rate;
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "EU"){
			EU_rate = ds_EM006PR.GetColumn(i,"ERATE");
			st_EU.Text = "\ "+EU_rate;
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "DM"){
			DM_rate = ds_EM006PR.GetColumn(i,"ERATE");
			st_DM.Text = "\ "+DM_rate;
		}
	}

	var yyyy0 = toNumber(substr(ca_YYYY.Value,0,4));
	var yyyy1 = yyyy0+1;

	var YM04 = substr(ca_YYYY.Value,0,4) + "04";
	var YM05 = substr(ca_YYYY.Value,0,4) + "05";
	var YM06 = substr(ca_YYYY.Value,0,4) + "06";
	var YM07 = substr(ca_YYYY.Value,0,4) + "07";
	var YM08 = substr(ca_YYYY.Value,0,4) + "08";
	var YM09 = substr(ca_YYYY.Value,0,4) + "09";
	var YM10 = substr(ca_YYYY.Value,0,4) + "10";
	var YM11 = substr(ca_YYYY.Value,0,4) + "11";
	var YM12 = substr(ca_YYYY.Value,0,4) + "12";
	var YM01 = yyyy1 + "01";
	var YM02 = yyyy1 + "02";
	var YM03 = yyyy1 + "03";

	var YMD_PRICE = yyyy1 + "0331"; //단가산출을 위한 기준일자

	var p_prd1,p_prd2;
	p_prd1 = iif (length(trim(ed_PRDCD1.Text)) == 0, ' ', trim(ed_PRDCD1.Text));
	p_prd2 = iif (length(trim(ed_PRDCD2.Text)) == 0 , '999', trim(ed_PRDCD2.Text));

	var sKind		= "getCYJC101Q";						
	var sMethodName = "service::pscstlib/selectCYJC101Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CYJC101Q_LIST=ds_CYJC101Q_LIST ds_CYJC101Q_PRICE=ds_CYJC101Q_PRICE";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " PLNGB=" + quote(cmbSelect.Value);
	sArgument += " D30PR=" + quote("F#D30PR");
	sArgument += " PRDCD1=" + quote(p_prd1);
	sArgument += " PRDCD2=" + quote(p_prd2);
	sArgument += " PLNYM04=" + quote(YM04);
	sArgument += " PLNYM05=" + quote(YM05);
	sArgument += " PLNYM06=" + quote(YM06);
	sArgument += " PLNYM07=" + quote(YM07);
	sArgument += " PLNYM08=" + quote(YM08);
	sArgument += " PLNYM09=" + quote(YM09);
	sArgument += " PLNYM10=" + quote(YM10);
	sArgument += " PLNYM11=" + quote(YM11);
	sArgument += " PLNYM12=" + quote(YM12);
	sArgument += " PLNYM01=" + quote(YM01);
	sArgument += " PLNYM02=" + quote(YM02);
	sArgument += " PLNYM03=" + quote(YM03);
	sArgument += " YMDPRICE=" + quote(YMD_PRICE);
	sArgument += " UDRATE=" + quote(UD_rate);
	sArgument += " JYRATE=" + quote(JY_rate);
	sArgument += " PDRATE=" + quote(PD_rate);
	sArgument += " EURATE=" + quote(EU_rate);
	sArgument += " DMRATE=" + quote(DM_rate);
	http.Sync=true;
	http.TimeOut = 900;
	//var str = dialog("Common::PGMLOAD_CST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.TimeOut = 300;
	http.Sync=false;
	/*
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	*/
}


