function set_Calculator(){

	var UD_rate = "0";
	var JY_rate = "0";
	var PD_rate = "0";
	var EU_rate = "0";
	var DM_rate = "0";
	for(var i=0;i<ds_EM006PR.rowcount;i++){
		if(ds_EM006PR.GetColumn(i,"CURC2") == "UD"){
			UD_rate = ds_EM006PR.GetColumn(i,"ERATE");
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "JY"){
			JY_rate = ds_EM006PR.GetColumn(i,"ERATE");
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "PD"){
			PD_rate = ds_EM006PR.GetColumn(i,"ERATE");
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "EU"){
			EU_rate = ds_EM006PR.GetColumn(i,"ERATE");
		}else if(ds_EM006PR.GetColumn(i,"CURC2") == "DM"){
			DM_rate = ds_EM006PR.GetColumn(i,"ERATE");
		}
	}

	for( i=0; i< ds_CYJC101Q_LIST.rowcount; i++){
		var p_IQY = 0;
		var p_Row=ds_CYJC101Q_PRICE.FindRow("PRTNO",ds_CYJC101Q_LIST.GetColumn(i,"PSPNO"));
		var p_CMPRICE = ds_CYJC101Q_PRICE.GetColumn(p_Row,"CMPRICE");
		var p_CMCRCY = trim(ds_CYJC101Q_PRICE.GetColumn(p_Row,"CMCRCY"));
		for(j=1; j<=12; j++){
			var p_IQY =toNumber(ds_CYJC101Q_LIST.GetColumn(i,"IQY"+lpad(j,"0",2)));
			if(p_CMCRCY=="UD"){
				ds_CYJC101Q_LIST.SetColumn(i,"IAM"+lpad(j,"0",2), parseInt(toNumber(p_IQY)* toNumber(p_CMPRICE)* toNumber(UD_rate)));
			}else if(p_CMCRCY=="JY"){
				ds_CYJC101Q_LIST.SetColumn(i,"IAM"+lpad(j,"0",2), parseInt(toNumber(p_IQY)* toNumber(p_CMPRICE)* toNumber(JY_rate)));
			}else if(p_CMCRCY=="EU"){
				ds_CYJC101Q_LIST.SetColumn(i,"IAM"+lpad(j,"0",2), parseInt(toNumber(p_IQY)* toNumber(p_CMPRICE)* toNumber(EU_rate)));
			}else if(p_CMCRCY=="PD"){
				ds_CYJC101Q_LIST.SetColumn(i,"IAM"+lpad(j,"0",2), parseInt(toNumber(p_IQY)* toNumber(p_CMPRICE)* toNumber(PD_rate)));
			}else if(p_CMCRCY=="DM"){
				ds_CYJC101Q_LIST.SetColumn(i,"IAM"+lpad(j,"0",2), parseInt(toNumber(p_IQY)* toNumber(p_CMPRICE)* toNumber(DM_rate)));
			}else if(p_CMCRCY=="KW"){
				ds_CYJC101Q_LIST.SetColumn(i,"IAM"+lpad(j,"0",2), parseInt(toNumber(p_IQY)* toNumber(p_CMPRICE)));
			}
		}
		ds_CYJC101Q_LIST.SetColumn(i,"CMPRICE",p_CMPRICE);
		ds_CYJC101Q_LIST.SetColumn(i,"CMCRCY",p_CMCRCY);

	}
	ds_CYJC101Q.Copy(ds_CYJC101Q_LIST);
}
