	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");

	//displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getFBRZ502Q(){
	if(length(trim(ed_PSPNO.Text)) <1){
		alert("품번은 필수입력입니다.");
		ed_PSPNO.SetFocus();
		return;
	}
	var FRSIDT = iif(length(trim(ca_FRSIDT.Value)) <1,"0",trim(ca_FRSIDT.Value));
	var TRSIDT = iif(length(trim(ca_TRSIDT.Value)) <1,"0",trim(ca_TRSIDT.Value));
	var FTRNDTZ = iif(length(trim(ca_FTRNDTZ.Value)) <1,"0",trim(ca_FTRNDTZ.Value));
	var TTRNDTZ = iif(length(trim(ca_TTRNDTZ.Value)) <1,"0",trim(ca_TTRNDTZ.Value));

	http.Sync = true;
	var sKind		= "getFBRZ502Q";
	var sMethodName = "service::psfaclib/selectFBRZ502Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FBRZ502Q=ds_FBRZ502Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	//sArgument += "@GUB=" + quote(cb_grpcd.Value);
	sArgument += "@PSPNO=" + quote(trim(ed_PSPNO.Text));
	sArgument += "@FRSIDT=" + quote(FRSIDT);  //출하일자 FROM
	sArgument += "@TRSIDT=" + quote(TRSIDT);  //출하일자 TO
	sArgument += "@FTRNTDZ=" + quote(FTRNDTZ);  //생산전표일 FROM
	sArgument += "@TTRNTDZ=" + quote(TTRNDTZ);  //생산전표일 TO
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}
}

