function setExcel()
{
	var fName   = "QR_TruckLoading완료조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|QR_TruckLoading완료조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

