/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "Nostock Update 입력조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|Nostock Update 입력조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

