}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "재고가시화 품번마스터";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|재고가시화 품번List";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

