function fn_Search()
{
	getFCKZ100();
}

function fn_Input()
{
	if(checkInsertCount("ds_FCKZ100")== false) return;
	if(!validationChk()){
		return;
	}
	if(ds_FCKZ100.rowcount < 1) fn_Search();  

	ds_FCKZ100.AddRow();
	ds_FCKZ100.SetColumn(ds_FCKZ100.currow,"COMPS",ComboBoxPlant.Value);	//사업부코드
	ds_FCKZ100.SetColumn(ds_FCKZ100.currow,"GRPCD",cd_GRPCD.Value);	//사업부코드
	ds_FCKZ100.SetColumn(ds_FCKZ100.currow,"MINST",0);	//최소재고일수
	ds_FCKZ100.SetColumn(ds_FCKZ100.currow,"MAXST",0);	//최고재고일수
	ds_FCKZ100.SetColumn(ds_FCKZ100.currow,"MAJOR",0);	//가시화여부
	ds_FCKZ100.SetColumn(ds_FCKZ100.currow,"BIGO1","");	//비고
}

function fn_Delete()
{
	if(ds_FCKZ100.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FCKZ100.DeleteRow(ds_FCKZ100.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

function fn_Save()
{
	setFCKZ100();
}

