function ds_FCKZ100_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "MINST"){
		if(varNewValue ==""){
			ds_FCKZ100.SetColumn(nRow, "MINST", 0);			
		}
	}
	if (strColumnID == "MAXST"){
		if(varNewValue ==""){
			ds_FCKZ100.SetColumn(nRow, "MAXST", 0);			
		}
	}
	if (strColumnID == "PRTNO"){
		//trace("OnColumn");
		if(varNewValue !=""){
			if(ds_BM008PR_PRD.rowcount == 0 ){
				ds_FCKZ100.SetColumn(nRow, "PRTNO", "");	
				return;
			}
			ds_FCKZ100.SetColumn(nRow, "ITDSC", ds_BM008PR_PRD.GetColumn(0,"ITDSC"));			
			ds_FCKZ100.SetColumn(nRow, "PRDCD", ds_BM008PR_PRD.GetColumn(0,"PRDCD"));			
			ds_FCKZ100.SetColumn(nRow, "PRDNM", ds_BM008PR_PRD.GetColumn(0,"PRDNM"));			
			ds_FCKZ100.SetColumn(nRow, "BIGO1", "");	
		}else{
			ds_FCKZ100.SetColumn(nRow, "ITDSC", "");			
			ds_FCKZ100.SetColumn(nRow, "PRDCD", "");			
			ds_FCKZ100.SetColumn(nRow, "PRDNM", "");	
			ds_FCKZ100.SetColumn(nRow, "BIGO1", "");	
		}
	}
	http.usewaitcursor = true;
}

function ds_FCKZ100_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PRTNO"){//품번 확인
		//trace("CanColumn");
		if(varNewValue!=""){
			if(ds_FCKZ100.FindRow("PRTNO",varNewValue) != -1){
				ds_BM008PR_PRD.ClearData();	//중복된 항목은 체크를 위함.
				alert("[알림]이미 등록된 품번입니다.");
				return;
			}

			http.Sync = true;
			var sKind		= "getHMD020PF";		
			var sMethodName = "service::pscomlib/selectBM008PR_PRD.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_BM008PR_PRD=ds_BM008PR_PRD";
			var sArgument   = "";
				sArgument  += " LIB1=" + quote(Lib1);
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " PRTNO=" + quote(varNewValue);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;

			if(ds_BM008PR_PRD.rowcount<1){
				alert("[알림]미등록된 품번입니다.");
				return false;
			}
			//KORNM=ds_BM008PR_PRD.GetColumn(0,"A2");
		}
	}
	http.usewaitcursor = true;
}
